import{_ as e,c as d,o as a,al as o}from"./chunks/framework.DM883ANZ.js";const T=JSON.parse('{"title":"Dialect: marsh","description":"","frontmatter":{},"headers":[],"relativePath":"en/messages/marsh.md","filePath":"en/messages/marsh.md"}'),i={name:"en/messages/marsh.md"};function r(n,t,s,c,_,l){return a(),d("div",null,[...t[0]||(t[0]=[o('<h1 id="dialect-marsh" tabindex="-1">Dialect: marsh <a class="header-anchor" href="#dialect-marsh" aria-label="Permalink to &quot;Dialect: marsh&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>This topic documents the version of the dialect file in the <a href="https://github.com/mavlink/mavlink" target="_blank" rel="noreferrer">mavlink/mavlink</a> Github repository, which may not be up to date with the file in the source repository (it is up to the dialect owner to push changes when needed). The source repo should be listed in the comments at the top of the XML definition file listed below (but may not be).</p></div><p>This topic is a human-readable form of the XML definition file: <a href="https://github.com/mavlink/mavlink/blob/master/message_definitions/v1.0/marsh.xml" target="_blank" rel="noreferrer">marsh.xml</a>.</p><p><span id="mav2_extension_field"></span></p><div class="info custom-block"><p class="custom-block-title">INFO</p><ul><li>MAVLink 2 <a href="./../guide/define_xml_element.html#message_extensions">extension fields</a> are displayed in blue.</li><li>Entities from dialects are displayed only as headings (with link to original)</li></ul></div><p><strong>Protocol dialect:</strong> 3</p><h2 id="mavlink-include-files" tabindex="-1">MAVLink Include Files <a class="header-anchor" href="#mavlink-include-files" aria-label="Permalink to &quot;MAVLink Include Files&quot;">​</a></h2><ul><li><a href="./../messages/common.html">common.xml</a></li></ul><h2 id="summary" tabindex="-1">Summary <a class="header-anchor" href="#summary" aria-label="Permalink to &quot;Summary&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Type</th><th>Defined</th><th>Included</th></tr></thead><tbody><tr><td><a href="#messages">Messages</a></td><td>5</td><td>234</td></tr><tr><td><a href="#enumerated-types">Enums</a></td><td>6</td><td>155</td></tr><tr><td><a href="#mav_commands">Commands</a></td><td>166</td><td>0</td></tr></tbody></table><p>The following sections list all entities in the dialect (both included and defined in this file).</p><h2 id="messages" tabindex="-1">Messages <a class="header-anchor" href="#messages" aria-label="Permalink to &quot;Messages&quot;">​</a></h2><h3 id="CONTROL_LOADING_AXIS" tabindex="-1">CONTROL_LOADING_AXIS (52501) — [WIP] <a class="header-anchor" href="#CONTROL_LOADING_AXIS" aria-label="Permalink to &quot;CONTROL_LOADING_AXIS (52501) — [WIP] {#CONTROL_LOADING_AXIS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Send data about a control axis from a control loading system. This is the primary message for logging data from <a href="#MARSH_TYPE_CONTROL_LOADING">MARSH_TYPE_CONTROL_LOADING</a>.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>Values</th><th>Description</th></tr></thead><tbody><tr><td>time_boot_ms</td><td><code>uint32_t</code></td><td>ms</td><td></td><td>Timestamp (time since system boot).</td></tr><tr><td>axis</td><td><code>uint8_t</code></td><td></td><td><a href="#CONTROL_AXIS">CONTROL_AXIS</a></td><td>Control axis on which the measurements were taken.</td></tr><tr><td>position</td><td><code>float</code></td><td>deg</td><td></td><td>Axis position</td></tr><tr><td>velocity</td><td><code>float</code></td><td>deg/s</td><td></td><td>Axis velocity</td></tr><tr><td>force</td><td><code>float</code></td><td></td><td></td><td>Force applied in the pilot in the direction of movement axis (not gripping force), measured at the position of pilot&#39;s third finger (ring). Unit N (Newton), currently not part of mavschema.xsd</td></tr></tbody></table><h3 id="MOTION_PLATFORM_STATE" tabindex="-1">MOTION_PLATFORM_STATE (52502) — [WIP] <a class="header-anchor" href="#MOTION_PLATFORM_STATE" aria-label="Permalink to &quot;MOTION_PLATFORM_STATE (52502) — [WIP] {#MOTION_PLATFORM_STATE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>State report for motion platform used for moving the cockpit with the pilot for motion cueing. This is the primary message for <a href="#MARSH_TYPE_MOTION_PLATFORM">MARSH_TYPE_MOTION_PLATFORM</a>.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>Values</th><th>Description</th></tr></thead><tbody><tr><td>time_boot_ms</td><td><code>uint32_t</code></td><td>ms</td><td></td><td>Timestamp (time since system boot).</td></tr><tr><td>health</td><td><code>uint8_t</code></td><td></td><td><a href="#MOTION_PLATFORM_HEALTH">MOTION_PLATFORM_HEALTH</a></td><td>Generic system health (error and warning) status.</td></tr><tr><td>mode</td><td><code>uint8_t</code></td><td></td><td><a href="#MOTION_PLATFORM_MODE">MOTION_PLATFORM_MODE</a></td><td>Generic system operating mode.</td></tr><tr><td>x</td><td><code>float</code></td><td>m</td><td></td><td>X axis (surge) position, positive forward.</td></tr><tr><td>y</td><td><code>float</code></td><td>m</td><td></td><td>Y axis (sway) position, positive right.</td></tr><tr><td>z</td><td><code>float</code></td><td>m</td><td></td><td>Z axis (heave) position, positive down.</td></tr><tr><td>roll</td><td><code>float</code></td><td>rad</td><td></td><td>Roll position, positive right.</td></tr><tr><td>pitch</td><td><code>float</code></td><td>rad</td><td></td><td>Pitch position, positive nose up.</td></tr><tr><td>yaw</td><td><code>float</code></td><td>rad</td><td></td><td>Yaw position, positive right.</td></tr><tr><td>vel_x</td><td><code>float</code></td><td>m/s</td><td></td><td>X axis (surge) velocity, positive forward.</td></tr><tr><td>vel_y</td><td><code>float</code></td><td>m/s</td><td></td><td>Y axis (sway) velocity, positive right.</td></tr><tr><td>vel_z</td><td><code>float</code></td><td>m/s</td><td></td><td>Z axis (heave) velocity, positive down.</td></tr><tr><td>vel_roll</td><td><code>float</code></td><td>rad/s</td><td></td><td>Roll velocity, positive right.</td></tr><tr><td>vel_pitch</td><td><code>float</code></td><td>rad/s</td><td></td><td>Pitch velocity, positive nose up.</td></tr><tr><td>vel_yaw</td><td><code>float</code></td><td>rad/s</td><td></td><td>Yaw velocity, positive right.</td></tr><tr><td>acc_x</td><td><code>float</code></td><td>m/s/s</td><td></td><td>X axis (surge) acceleration, positive forward.</td></tr><tr><td>acc_y</td><td><code>float</code></td><td>m/s/s</td><td></td><td>Y axis (sway) acceleration, positive right.</td></tr><tr><td>acc_z</td><td><code>float</code></td><td>m/s/s</td><td></td><td>Z axis (heave) acceleration, positive down.</td></tr><tr><td>acc_roll</td><td><code>float</code></td><td></td><td></td><td>Roll acceleration, positive right. Unit rad/s/s, currently not part of mavschema.xsd</td></tr><tr><td>acc_pitch</td><td><code>float</code></td><td></td><td></td><td>Pitch acceleration, positive nose up. Unit rad/s/s, currently not part of mavschema.xsd</td></tr><tr><td>acc_yaw</td><td><code>float</code></td><td></td><td></td><td>Yaw acceleration, positive right. Unit rad/s/s, currently not part of mavschema.xsd</td></tr></tbody></table><h3 id="REXROTH_MOTION_PLATFORM" tabindex="-1">REXROTH_MOTION_PLATFORM (52503) — [WIP] <a class="header-anchor" href="#REXROTH_MOTION_PLATFORM" aria-label="Permalink to &quot;REXROTH_MOTION_PLATFORM (52503) — [WIP] {#REXROTH_MOTION_PLATFORM}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>State report specific for eMotion Motion System by Bosch Rexroth B.V. Values applicable to motion platforms in general are sent in <a href="#MOTION_PLATFORM_STATE">MOTION_PLATFORM_STATE</a> with the same timestamp. Actuators are numbered in a clockwise direction when looking from above, starting from the front right. Actuator position is 0 when actuator is in mid-stroke.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>Description</th></tr></thead><tbody><tr><td>time_boot_ms</td><td><code>uint32_t</code></td><td>ms</td><td>Timestamp (time since system boot).</td></tr><tr><td>frame_count</td><td><code>uint32_t</code></td><td></td><td>Number of message as sent by the Motion System.</td></tr><tr><td>motion_status</td><td><code>uint32_t</code></td><td></td><td>Motion Status variable as sent by the system.</td></tr><tr><td>error_code</td><td><code>uint8_t</code></td><td></td><td>Error code extracted from motion status.</td></tr><tr><td>actuator1</td><td><code>float</code></td><td>m</td><td>Current actuator 1 position.</td></tr><tr><td>actuator2</td><td><code>float</code></td><td>m</td><td>Current actuator 2 position.</td></tr><tr><td>actuator3</td><td><code>float</code></td><td>m</td><td>Current actuator 3 position.</td></tr><tr><td>actuator4</td><td><code>float</code></td><td>m</td><td>Current actuator 4 position.</td></tr><tr><td>actuator5</td><td><code>float</code></td><td>m</td><td>Current actuator 5 position.</td></tr><tr><td>actuator6</td><td><code>float</code></td><td>m</td><td>Current actuator 6 position.</td></tr><tr><td>platform_setpoint_x</td><td><code>float</code></td><td>m</td><td>X axis (surge) platform setpoint, positive forward.</td></tr><tr><td>platform_setpoint_y</td><td><code>float</code></td><td>m</td><td>Y axis (sway) platform setpoint, positive right.</td></tr><tr><td>platform_setpoint_z</td><td><code>float</code></td><td>m</td><td>Z axis (heave) platform setpoint, positive down.</td></tr><tr><td>platform_setpoint_roll</td><td><code>float</code></td><td>rad</td><td>Roll platform setpoint, positive right.</td></tr><tr><td>platform_setpoint_pitch</td><td><code>float</code></td><td>rad</td><td>Pitch platform setpoint, positive nose up.</td></tr><tr><td>platform_setpoint_yaw</td><td><code>float</code></td><td>rad</td><td>Yaw platform setpoint, positive right.</td></tr><tr><td>effect_setpoint_x</td><td><code>float</code></td><td>m</td><td>X axis (surge) special effect setpoint, positive forward.</td></tr><tr><td>effect_setpoint_y</td><td><code>float</code></td><td>m</td><td>Y axis (sway) special effect setpoint, positive right.</td></tr><tr><td>effect_setpoint_z</td><td><code>float</code></td><td>m</td><td>Z axis (heave) special effect setpoint, positive down.</td></tr><tr><td>effect_setpoint_roll</td><td><code>float</code></td><td>rad</td><td>Roll special effect setpoint, positive right.</td></tr><tr><td>effect_setpoint_pitch</td><td><code>float</code></td><td>rad</td><td>Pitch special effect setpoint, positive nose up.</td></tr><tr><td>effect_setpoint_yaw</td><td><code>float</code></td><td>rad</td><td>Yaw special effect setpoint, positive right.</td></tr></tbody></table><h3 id="MOTION_CUE_EXTRA" tabindex="-1">MOTION_CUE_EXTRA (52504) <a class="header-anchor" href="#MOTION_CUE_EXTRA" aria-label="Permalink to &quot;MOTION_CUE_EXTRA (52504) {#MOTION_CUE_EXTRA}&quot;">​</a></h3><p>These values are an extra cue that should be added to accelerations and rotations etc. resulting from aircraft state, with the resulting cue being the sum of the latest aircraft and extra values. An example use case would be a cockpit shaker.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>Description</th></tr></thead><tbody><tr><td>time_boot_ms</td><td><code>uint32_t</code></td><td>ms</td><td>Timestamp (time since system boot).</td></tr><tr><td>vel_roll</td><td><code>float</code></td><td>rad/s</td><td>Roll velocity, positive right.</td></tr><tr><td>vel_pitch</td><td><code>float</code></td><td>rad/s</td><td>Pitch velocity, positive nose up.</td></tr><tr><td>vel_yaw</td><td><code>float</code></td><td>rad/s</td><td>Yaw velocity, positive right.</td></tr><tr><td>acc_x</td><td><code>float</code></td><td>m/s/s</td><td>X axis (surge) acceleration, positive forward.</td></tr><tr><td>acc_y</td><td><code>float</code></td><td>m/s/s</td><td>Y axis (sway) acceleration, positive right.</td></tr><tr><td>acc_z</td><td><code>float</code></td><td>m/s/s</td><td>Z axis (heave) acceleration, positive down.</td></tr></tbody></table><h3 id="EYE_TRACKING_DATA" tabindex="-1">EYE_TRACKING_DATA (52505) — [WIP] <a class="header-anchor" href="#EYE_TRACKING_DATA" aria-label="Permalink to &quot;EYE_TRACKING_DATA (52505) — [WIP] {#EYE_TRACKING_DATA}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Data for tracking of pilot eye gaze. This is the primary message for <a href="#MARSH_TYPE_EYE_TRACKER">MARSH_TYPE_EYE_TRACKER</a>.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>Description</th></tr></thead><tbody><tr><td>time_usec</td><td><code>uint64_t</code></td><td>us</td><td>Timestamp (time since system boot).</td></tr><tr><td>sensor_id</td><td><code>uint8_t</code></td><td></td><td>Sensor ID, used for identifying the device and/or person tracked. Set to zero if unknown/unused.<br>Messages with same value are from the same source (instance).</td></tr><tr><td>gaze_origin_x</td><td><code>float</code></td><td>m</td><td>X axis of gaze origin point, NaN if unknown. The reference system depends on specific application.</td></tr><tr><td>gaze_origin_y</td><td><code>float</code></td><td>m</td><td>Y axis of gaze origin point, NaN if unknown. The reference system depends on specific application.</td></tr><tr><td>gaze_origin_z</td><td><code>float</code></td><td>m</td><td>Z axis of gaze origin point, NaN if unknown. The reference system depends on specific application.</td></tr><tr><td>gaze_direction_x</td><td><code>float</code></td><td></td><td>X axis of gaze direction vector, expected to be normalized to unit magnitude, NaN if unknown. The reference system should match origin point.</td></tr><tr><td>gaze_direction_y</td><td><code>float</code></td><td></td><td>Y axis of gaze direction vector, expected to be normalized to unit magnitude, NaN if unknown. The reference system should match origin point.</td></tr><tr><td>gaze_direction_z</td><td><code>float</code></td><td></td><td>Z axis of gaze direction vector, expected to be normalized to unit magnitude, NaN if unknown. The reference system should match origin point.</td></tr><tr><td>video_gaze_x</td><td><code>float</code></td><td></td><td>Gaze focal point on video feed x value (normalized 0..1, 0 is left, 1 is right), NaN if unknown</td></tr><tr><td>video_gaze_y</td><td><code>float</code></td><td></td><td>Gaze focal point on video feed y value (normalized 0..1, 0 is top, 1 is bottom), NaN if unknown</td></tr><tr><td>surface_id</td><td><code>uint8_t</code></td><td></td><td>Identifier of surface for 2D gaze point, or an identified region when surface point is invalid. Set to zero if unknown/unused.</td></tr><tr><td>surface_gaze_x</td><td><code>float</code></td><td></td><td>Gaze focal point on surface x value (normalized 0..1, 0 is left, 1 is right), NaN if unknown</td></tr><tr><td>surface_gaze_y</td><td><code>float</code></td><td></td><td>Gaze focal point on surface y value (normalized 0..1, 0 is top, 1 is bottom), NaN if unknown</td></tr></tbody></table><h2 id="enumerated-types" tabindex="-1">Enumerated Types <a class="header-anchor" href="#enumerated-types" aria-label="Permalink to &quot;Enumerated Types&quot;">​</a></h2><h3 id="MARSH_TYPE" tabindex="-1">MARSH_TYPE <a class="header-anchor" href="#MARSH_TYPE" aria-label="Permalink to &quot;MARSH_TYPE {#MARSH_TYPE}&quot;">​</a></h3><p>Component types for different nodes of the simulator network (flight model, controls, visualisation etc.). Components will always receive messages from the Manager relevant for their type. Only the first component in a network with a given system ID and type will have its messages forwarded by the Manager, all other ones will only be treated as output (will be shadowed). This enum is an extension of <a href="#MAV_TYPE">MAV_TYPE</a> documented at <a href="https://mavlink.io/en/messages/minimal.html#MAV_TYPE" target="_blank" rel="noreferrer">https://mavlink.io/en/messages/minimal.html#MAV_TYPE</a></p><table tabindex="0"><thead><tr><th>Value</th><th>Name</th><th>Description</th></tr></thead><tbody><tr><td><a id="MARSH_TYPE_MANAGER"></a>100</td><td><a href="#MARSH_TYPE_MANAGER">MARSH_TYPE_MANAGER</a></td><td>The simulation manager responsible for routing packets between different nodes. Typically MARSH Manager, see <a href="https://marsh-sim.github.io/manager.html" target="_blank" rel="noreferrer">https://marsh-sim.github.io/manager.html</a></td></tr><tr><td><a id="MARSH_TYPE_FLIGHT_MODEL"></a>101</td><td><a href="#MARSH_TYPE_FLIGHT_MODEL">MARSH_TYPE_FLIGHT_MODEL</a></td><td>Component simulating flight dynamics of the aircraft.</td></tr><tr><td><a id="MARSH_TYPE_CONTROLS"></a>102</td><td><a href="#MARSH_TYPE_CONTROLS">MARSH_TYPE_CONTROLS</a></td><td>Component providing pilot control inputs.</td></tr><tr><td><a id="MARSH_TYPE_VISUALISATION"></a>103</td><td><a href="#MARSH_TYPE_VISUALISATION">MARSH_TYPE_VISUALISATION</a></td><td>Component showing the visual situation to the pilot.</td></tr><tr><td><a id="MARSH_TYPE_INSTRUMENTS"></a>104</td><td><a href="#MARSH_TYPE_INSTRUMENTS">MARSH_TYPE_INSTRUMENTS</a></td><td>Component implementing pilot instrument panel.</td></tr><tr><td><a id="MARSH_TYPE_MOTION_PLATFORM"></a>105</td><td><a href="#MARSH_TYPE_MOTION_PLATFORM">MARSH_TYPE_MOTION_PLATFORM</a></td><td>Component that moves the entire cockpit for motion cueing.</td></tr><tr><td><a id="MARSH_TYPE_GSEAT"></a>106</td><td><a href="#MARSH_TYPE_GSEAT">MARSH_TYPE_GSEAT</a></td><td>Component for in-seat motion cueing.</td></tr><tr><td><a id="MARSH_TYPE_EYE_TRACKER"></a>107</td><td><a href="#MARSH_TYPE_EYE_TRACKER">MARSH_TYPE_EYE_TRACKER</a></td><td>Component providing gaze data of pilot eyes.</td></tr><tr><td><a id="MARSH_TYPE_CONTROL_LOADING"></a>108</td><td><a href="#MARSH_TYPE_CONTROL_LOADING">MARSH_TYPE_CONTROL_LOADING</a></td><td>Component measuring and actuating forces on pilot control inputs.</td></tr><tr><td><a id="MARSH_TYPE_VIBRATION_SOURCE"></a>109</td><td><a href="#MARSH_TYPE_VIBRATION_SOURCE">MARSH_TYPE_VIBRATION_SOURCE</a></td><td>Component providing vibrations for system identification, road rumble, gusts, etc.</td></tr><tr><td><a id="MARSH_TYPE_PILOT_TARGET"></a>110</td><td><a href="#MARSH_TYPE_PILOT_TARGET">MARSH_TYPE_PILOT_TARGET</a></td><td>Component providing target for the pilot to follow like controls positions, aircraft state, ILS path etc.</td></tr><tr><td><a id="MARSH_TYPE_EXPERIMENT_DIRECTOR"></a>111</td><td><a href="#MARSH_TYPE_EXPERIMENT_DIRECTOR">MARSH_TYPE_EXPERIMENT_DIRECTOR</a></td><td>Principal component controlling the main scenario of a given test, (unlike lower level <a href="#MARSH_TYPE_PILOT_TARGET">MARSH_TYPE_PILOT_TARGET</a> or <a href="#MARSH_TYPE_MANAGER">MARSH_TYPE_MANAGER</a> for overall communication).</td></tr></tbody></table><h3 id="MARSH_MODE_FLAGS" tabindex="-1">MARSH_MODE_FLAGS — [WIP] <a class="header-anchor" href="#MARSH_MODE_FLAGS" aria-label="Permalink to &quot;MARSH_MODE_FLAGS — [WIP] {#MARSH_MODE_FLAGS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>These values are MARSH-specific modes intended to be sent in custom_mode field of HEARTBEAT message.</p><p>Prefer defining values in the most significant byte (between 2^24 and 2^31) to leave the lower three bytes to contain a message id</p><table tabindex="0"><thead><tr><th>Value</th><th>Name</th><th>Description</th></tr></thead><tbody><tr><td><a id="MARSH_MODE_SINGLE_MESSAGE"></a>16777216</td><td><a href="#MARSH_MODE_SINGLE_MESSAGE">MARSH_MODE_SINGLE_MESSAGE</a></td><td>Request Manager to only send one specific message, advised for very resource limited nodes or with control flow limitations like Simulink.<br>That message id should be in the lower three bytes of the mode, which can be done by adding it to the flags.</td></tr><tr><td><a id="MARSH_MODE_ALL_MESSAGES"></a>33554432</td><td><a href="#MARSH_MODE_ALL_MESSAGES">MARSH_MODE_ALL_MESSAGES</a></td><td>Request Manager to send every message going out to any of the clients.</td></tr></tbody></table><h3 id="CONTROL_AXIS" tabindex="-1">CONTROL_AXIS <a class="header-anchor" href="#CONTROL_AXIS" aria-label="Permalink to &quot;CONTROL_AXIS {#CONTROL_AXIS}&quot;">​</a></h3><p>Specific axis of pilot control inputs, with order corresponding to x, y, z, r fields in <a href="#MANUAL_CONTROL">MANUAL_CONTROL</a> message.</p><table tabindex="0"><thead><tr><th>Value</th><th>Name</th><th>Description</th></tr></thead><tbody><tr><td><a id="CONTROL_AXIS_ROLL"></a>0</td><td><a href="#CONTROL_AXIS_ROLL">CONTROL_AXIS_ROLL</a></td><td>Roll axis, with positive values corresponding to stick right movement, causing the vehicle to roll right. For helicopters this is lateral cyclic.</td></tr><tr><td><a id="CONTROL_AXIS_PITCH"></a>1</td><td><a href="#CONTROL_AXIS_PITCH">CONTROL_AXIS_PITCH</a></td><td>Pitch axis, with positive values corresponding to stick forward movement, causing the vehicle to move nose down. For helicopters this is longitudinal cyclic.</td></tr><tr><td><a id="CONTROL_AXIS_THRUST"></a>2</td><td><a href="#CONTROL_AXIS_THRUST">CONTROL_AXIS_THRUST</a></td><td>Main thrust, with positive values corresponding to going faster and higher. For helicopters this is collective.</td></tr><tr><td><a id="CONTROL_AXIS_YAW"></a>3</td><td><a href="#CONTROL_AXIS_YAW">CONTROL_AXIS_YAW</a></td><td>Yaw axis, with positive values corresponding to pushing right pedal, causing the vehicle to face right direction. For helicopters this is tail collective.</td></tr></tbody></table><h3 id="MARSH_MANUAL_SETPOINT_MODE" tabindex="-1">MARSH_MANUAL_SETPOINT_MODE <a class="header-anchor" href="#MARSH_MANUAL_SETPOINT_MODE" aria-label="Permalink to &quot;MARSH_MANUAL_SETPOINT_MODE {#MARSH_MANUAL_SETPOINT_MODE}&quot;">​</a></h3><p>Usage of <a href="#MANUAL_SETPOINT">MANUAL_SETPOINT</a> message, sent in mode_switch field.</p><table tabindex="0"><thead><tr><th>Value</th><th>Name</th><th>Description</th></tr></thead><tbody><tr><td><a id="MARSH_MANUAL_SETPOINT_MODE_TARGET"></a>0</td><td><a href="#MARSH_MANUAL_SETPOINT_MODE_TARGET">MARSH_MANUAL_SETPOINT_MODE_TARGET</a></td><td>Values for target inceptors positions that the pilot should follow.</td></tr><tr><td><a id="MARSH_MANUAL_SETPOINT_MODE_TRIM"></a>1</td><td><a href="#MARSH_MANUAL_SETPOINT_MODE_TRIM">MARSH_MANUAL_SETPOINT_MODE_TRIM</a></td><td>Values for inceptors trim positions, the exact meaning depends on the flight model.</td></tr></tbody></table><h3 id="MOTION_PLATFORM_MODE" tabindex="-1">MOTION_PLATFORM_MODE <a class="header-anchor" href="#MOTION_PLATFORM_MODE" aria-label="Permalink to &quot;MOTION_PLATFORM_MODE {#MOTION_PLATFORM_MODE}&quot;">​</a></h3><p>Mode of a motion platform system.</p><table tabindex="0"><thead><tr><th>Value</th><th>Name</th><th>Description</th></tr></thead><tbody><tr><td><a id="MOTION_PLATFORM_MODE_UNKNOWN"></a>0</td><td><a href="#MOTION_PLATFORM_MODE_UNKNOWN">MOTION_PLATFORM_MODE_UNKNOWN</a></td><td>Mode information is unsupported on this device.</td></tr><tr><td><a id="MOTION_PLATFORM_MODE_UNINITIALIZED"></a>1</td><td><a href="#MOTION_PLATFORM_MODE_UNINITIALIZED">MOTION_PLATFORM_MODE_UNINITIALIZED</a></td><td>Mode is currently not available, but may be in different condition.</td></tr><tr><td><a id="MOTION_PLATFORM_MODE_OFF"></a>2</td><td><a href="#MOTION_PLATFORM_MODE_OFF">MOTION_PLATFORM_MODE_OFF</a></td><td>Platform actuators are turned off, but control system is responsive.</td></tr><tr><td><a id="MOTION_PLATFORM_MODE_SETTLED"></a>3</td><td><a href="#MOTION_PLATFORM_MODE_SETTLED">MOTION_PLATFORM_MODE_SETTLED</a></td><td>Platform is in the lowest position and/or locked, appropriate for personnel entry.</td></tr><tr><td><a id="MOTION_PLATFORM_MODE_NEUTRAL"></a>4</td><td><a href="#MOTION_PLATFORM_MODE_NEUTRAL">MOTION_PLATFORM_MODE_NEUTRAL</a></td><td>Platform is in a neutral reference position, not accepting movement commands.</td></tr><tr><td><a id="MOTION_PLATFORM_MODE_FROZEN"></a>5</td><td><a href="#MOTION_PLATFORM_MODE_FROZEN">MOTION_PLATFORM_MODE_FROZEN</a></td><td>Platform is stopped in any position, not accepting movement commands.</td></tr><tr><td><a id="MOTION_PLATFORM_MODE_ENGAGED"></a>6</td><td><a href="#MOTION_PLATFORM_MODE_ENGAGED">MOTION_PLATFORM_MODE_ENGAGED</a></td><td>Platform is in any position, accepting movement commands.</td></tr></tbody></table><h3 id="MOTION_PLATFORM_HEALTH" tabindex="-1">MOTION_PLATFORM_HEALTH <a class="header-anchor" href="#MOTION_PLATFORM_HEALTH" aria-label="Permalink to &quot;MOTION_PLATFORM_HEALTH {#MOTION_PLATFORM_HEALTH}&quot;">​</a></h3><p>General error state of a motion platform system.</p><table tabindex="0"><thead><tr><th>Value</th><th>Name</th><th>Description</th></tr></thead><tbody><tr><td><a id="MOTION_PLATFORM_HEALTH_OK"></a>0</td><td><a href="#MOTION_PLATFORM_HEALTH_OK">MOTION_PLATFORM_HEALTH_OK</a></td><td>System is operating correctly.</td></tr><tr><td><a id="MOTION_PLATFORM_HEALTH_WARNING"></a>1</td><td><a href="#MOTION_PLATFORM_HEALTH_WARNING">MOTION_PLATFORM_HEALTH_WARNING</a></td><td>There is at least one warning present, but operation can be continued.</td></tr><tr><td><a id="MOTION_PLATFORM_HEALTH_ERROR"></a>2</td><td><a href="#MOTION_PLATFORM_HEALTH_ERROR">MOTION_PLATFORM_HEALTH_ERROR</a></td><td>There is a failure or misconfiguration that requires operator&#39;s attention for correct operation.</td></tr><tr><td><a id="MOTION_PLATFORM_HEALTH_CRITICAL"></a>3</td><td><a href="#MOTION_PLATFORM_HEALTH_CRITICAL">MOTION_PLATFORM_HEALTH_CRITICAL</a></td><td>There is a major failure that requires immediate operator action to maintain safety.</td></tr></tbody></table><h2 id="mav_commands" tabindex="-1">Commands (MAV_CMD) <a class="header-anchor" href="#mav_commands" aria-label="Permalink to &quot;Commands (MAV_CMD) {#mav_commands}&quot;">​</a></h2>',53)])])}const O=e(i,[["render",r]]);export{T as __pageData,O as default};
