import{_ as e,c as a,a8 as d,o as r}from"./chunks/framework.CnRC9NRC.js";const l=JSON.parse('{"title":"Dialect: development","description":"","frontmatter":{},"headers":[],"relativePath":"zh/messages/development.md","filePath":"zh/messages/development.md"}'),o={name:"zh/messages/development.md"};function n(i,t,s,_,A,T){return r(),a("div",null,t[0]||(t[0]=[d('<h1 id="dialect-development" tabindex="-1">Dialect: development <a class="header-anchor" href="#dialect-development" aria-label="Permalink to &quot;Dialect: development&quot;">​</a></h1><p>This dialect contains messages that are proposed for inclusion in the <a href="./standard.html">standard set</a>, in order to ease development of prototype implementations. They should be considered a &#39;work in progress&#39; and not included in production builds.</p><p>This topic is a human-readable form of the XML definition file: <a href="https://github.com/mavlink/mavlink/blob/master/message_definitions/v1.0/development.xml" target="_blank" rel="noreferrer">development.xml</a>.</p><p><span id="mav2_extension_field"></span></p><blockquote><p><strong>Note</strong></p><ul><li>MAVLink 2 <a href="./../guide/define_xml_element.html#message_extensions">extension fields</a> are displayed in blue.</li><li>Entities from dialects are displayed only as headings (with link to original)</li></ul></blockquote><p><strong>Protocol dialect:</strong> 0</p><p><strong>Protocol version:</strong> 0</p><h2 id="mavlink-include-files" tabindex="-1">MAVLink Include Files <a class="header-anchor" href="#mavlink-include-files" aria-label="Permalink to &quot;MAVLink Include Files&quot;">​</a></h2><ul><li><a href="./../messages/common.html">common.xml</a></li></ul><h2 id="概览" tabindex="-1">概览 <a class="header-anchor" href="#概览" aria-label="Permalink to &quot;概览&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Type</th><th>Defined</th><th>Included</th></tr></thead><tbody><tr><td><a href="#messages">Messages</a></td><td>16</td><td>226</td></tr><tr><td><a href="#enumerated-types">Enums</a></td><td>15</td><td>144</td></tr><tr><td><a href="#mav_commands">Commands</a></td><td>173</td><td>0</td></tr></tbody></table><p>The following sections list all entities in the dialect (both included and defined in this file).</p><h2 id="消息" tabindex="-1">消息 <a class="header-anchor" href="#消息" aria-label="Permalink to &quot;消息&quot;">​</a></h2><h3 id="PARAM_ACK_TRANSACTION" tabindex="-1">PARAM_ACK_TRANSACTION (19) — [WIP] <a class="header-anchor" href="#PARAM_ACK_TRANSACTION" aria-label="Permalink to &quot;PARAM_ACK_TRANSACTION (19) — [WIP] {#PARAM_ACK_TRANSACTION}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Response from a <a href="#PARAM_SET">PARAM_SET</a> message when it is used in a transaction.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>target_system</td><td><code>uint8_t</code></td><td></td><td>Id of system that sent <a href="#PARAM_SET">PARAM_SET</a> message.</td></tr><tr><td>target_component</td><td><code>uint8_t</code></td><td></td><td>Id of system that sent <a href="#PARAM_SET">PARAM_SET</a> message.</td></tr><tr><td>param_id</td><td><code>char[16]</code></td><td></td><td>Parameter id, terminated by NULL if the length is less than 16 human-readable chars and WITHOUT null termination (NULL) byte if the length is exactly 16 chars - applications have to provide 16+1 bytes storage if the ID is stored as string</td></tr><tr><td>param_value</td><td><code>float</code></td><td></td><td>Parameter value (new value if <a href="#PARAM_ACCEPTED">PARAM_ACCEPTED</a>, current value otherwise)</td></tr><tr><td>param_type</td><td><code>uint8_t</code></td><td><a href="#MAV_PARAM_TYPE">MAV_PARAM_TYPE</a></td><td>Parameter type.</td></tr><tr><td>param_result</td><td><code>uint8_t</code></td><td><a href="#PARAM_ACK">PARAM_ACK</a></td><td>Result code.</td></tr></tbody></table><h3 id="AIRSPEED" tabindex="-1">AIRSPEED (295) — [WIP] <a class="header-anchor" href="#AIRSPEED" aria-label="Permalink to &quot;AIRSPEED (295) — [WIP] {#AIRSPEED}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Airspeed information from a sensor.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>id</td><td><code>uint8_t</code></td><td></td><td></td><td>Sensor ID.<br>Messages with same value are from the same source (instance).</td></tr><tr><td>airspeed</td><td><code>float</code></td><td>m/s</td><td></td><td>Calibrated airspeed (CAS).</td></tr><tr><td>temperature</td><td><code>int16_t</code></td><td>cdegC</td><td></td><td>Temperature. INT16_MAX for value unknown/not supplied.</td></tr><tr><td>raw_press</td><td><code>float</code></td><td>hPa</td><td></td><td>Raw differential pressure. NaN for value unknown/not supplied.</td></tr><tr><td>flags</td><td><code>uint8_t</code></td><td></td><td><a href="#AIRSPEED_SENSOR_FLAGS">AIRSPEED_SENSOR_FLAGS</a></td><td>Airspeed sensor flags.</td></tr></tbody></table><h3 id="SET_VELOCITY_LIMITS" tabindex="-1">SET_VELOCITY_LIMITS (354) — [WIP] <a class="header-anchor" href="#SET_VELOCITY_LIMITS" aria-label="Permalink to &quot;SET_VELOCITY_LIMITS (354) — [WIP] {#SET_VELOCITY_LIMITS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Set temporary maximum limits for horizontal speed, vertical speed and yaw rate.</p><p>The consumer must stream the current limits in <a href="#VELOCITY_LIMITS">VELOCITY_LIMITS</a> at 1 Hz or more (when limits are being set). The consumer should latch the limits until a new limit is received or the mode is changed.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>描述</th></tr></thead><tbody><tr><td>target_system</td><td><code>uint8_t</code></td><td></td><td>System ID (0 for broadcast).</td></tr><tr><td>target_component</td><td><code>uint8_t</code></td><td></td><td>Component ID (0 for broadcast).</td></tr><tr><td>horizontal_speed_limit</td><td><code>float</code></td><td>m/s</td><td>Limit for horizontal movement in <a href="#MAV_FRAME_LOCAL_NED">MAV_FRAME_LOCAL_NED</a>. NaN: Field not used (ignore)</td></tr><tr><td>vertical_speed_limit</td><td><code>float</code></td><td>m/s</td><td>Limit for vertical movement in <a href="#MAV_FRAME_LOCAL_NED">MAV_FRAME_LOCAL_NED</a>. NaN: Field not used (ignore)</td></tr><tr><td>yaw_rate_limit</td><td><code>float</code></td><td>rad/s</td><td>Limit for vehicle turn rate around its yaw axis. NaN: Field not used (ignore)</td></tr></tbody></table><h3 id="VELOCITY_LIMITS" tabindex="-1">VELOCITY_LIMITS (355) — [WIP] <a class="header-anchor" href="#VELOCITY_LIMITS" aria-label="Permalink to &quot;VELOCITY_LIMITS (355) — [WIP] {#VELOCITY_LIMITS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Current limits for horizontal speed, vertical speed and yaw rate, as set by <a href="#SET_VELOCITY_LIMITS">SET_VELOCITY_LIMITS</a>.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>描述</th></tr></thead><tbody><tr><td>horizontal_speed_limit</td><td><code>float</code></td><td>m/s</td><td>Limit for horizontal movement in <a href="#MAV_FRAME_LOCAL_NED">MAV_FRAME_LOCAL_NED</a>. NaN: No limit applied</td></tr><tr><td>vertical_speed_limit</td><td><code>float</code></td><td>m/s</td><td>Limit for vertical movement in <a href="#MAV_FRAME_LOCAL_NED">MAV_FRAME_LOCAL_NED</a>. NaN: No limit applied</td></tr><tr><td>yaw_rate_limit</td><td><code>float</code></td><td>rad/s</td><td>Limit for vehicle turn rate around its yaw axis. NaN: No limit applied</td></tr></tbody></table><h3 id="FIGURE_EIGHT_EXECUTION_STATUS" tabindex="-1">FIGURE_EIGHT_EXECUTION_STATUS (361) — [WIP] <a class="header-anchor" href="#FIGURE_EIGHT_EXECUTION_STATUS" aria-label="Permalink to &quot;FIGURE_EIGHT_EXECUTION_STATUS (361) — [WIP] {#FIGURE_EIGHT_EXECUTION_STATUS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Vehicle status report that is sent out while figure eight execution is in progress (see <a href="#MAV_CMD_DO_FIGURE_EIGHT">MAV_CMD_DO_FIGURE_EIGHT</a>). This may typically send at low rates: of the order of 2Hz.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>time_usec</td><td><code>uint64_t</code></td><td>us</td><td></td><td>Timestamp (UNIX Epoch time or time since system boot). The receiving end can infer timestamp format (since 1.1.1970 or since system boot) by checking for the magnitude of the number.</td></tr><tr><td>major_radius</td><td><code>float</code></td><td>m</td><td></td><td>Major axis radius of the figure eight. Positive: orbit the north circle clockwise. Negative: orbit the north circle counter-clockwise.</td></tr><tr><td>minor_radius</td><td><code>float</code></td><td>m</td><td></td><td>Minor axis radius of the figure eight. Defines the radius of two circles that make up the figure.</td></tr><tr><td>orientation</td><td><code>float</code></td><td>rad</td><td></td><td>Orientation of the figure eight major axis with respect to true north in [-pi,pi).</td></tr><tr><td>frame</td><td><code>uint8_t</code></td><td></td><td><a href="#MAV_FRAME">MAV_FRAME</a></td><td>The coordinate system of the fields: x, y, z.</td></tr><tr><td>x</td><td><code>int32_t</code></td><td></td><td></td><td>X coordinate of center point. Coordinate system depends on frame field.</td></tr><tr><td>y</td><td><code>int32_t</code></td><td></td><td></td><td>Y coordinate of center point. Coordinate system depends on frame field.</td></tr><tr><td>z</td><td><code>float</code></td><td>m</td><td></td><td>Altitude of center point. Coordinate system depends on frame field.</td></tr></tbody></table><h3 id="BATTERY_STATUS_V2" tabindex="-1">BATTERY_STATUS_V2 (369) — [WIP] <a class="header-anchor" href="#BATTERY_STATUS_V2" aria-label="Permalink to &quot;BATTERY_STATUS_V2 (369) — [WIP] {#BATTERY_STATUS_V2}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Battery dynamic information.</p><p>This should be streamed (nominally at 1Hz). Static/invariant battery information is sent in <a href="#BATTERY_INFO">BATTERY_INFO</a>. Note that smart batteries should set the <a href="#MAV_BATTERY_STATUS_FLAGS_CAPACITY_RELATIVE_TO_FULL">MAV_BATTERY_STATUS_FLAGS_CAPACITY_RELATIVE_TO_FULL</a> bit to indicate that supplied capacity values are relative to a battery that is known to be full. Power monitors would not set this bit, indicating that capacity_consumed is relative to drone power-on, and that other values are estimated based on the assumption that the battery was full on power-on.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>id</td><td><code>uint8_t</code></td><td></td><td></td><td>Battery ID<br>Messages with same value are from the same source (instance).</td></tr><tr><td>temperature</td><td><code>int16_t</code></td><td>cdegC</td><td>invalid:INT16_MAX</td><td>Temperature of the whole battery pack (not internal electronics). INT16_MAX field not provided.</td></tr><tr><td>voltage</td><td><code>float</code></td><td>V</td><td>invalid:NaN</td><td>Battery voltage (total). NaN: field not provided.</td></tr><tr><td>current</td><td><code>float</code></td><td>A</td><td>invalid:NaN</td><td>Battery current (through all cells/loads). Positive value when discharging and negative if charging. NaN: field not provided.</td></tr><tr><td>capacity_consumed</td><td><code>float</code></td><td>Ah</td><td>invalid:NaN</td><td>Consumed charge. NaN: field not provided. This is either the consumption since power-on or since the battery was full, depending on the value of <a href="#MAV_BATTERY_STATUS_FLAGS_CAPACITY_RELATIVE_TO_FULL">MAV_BATTERY_STATUS_FLAGS_CAPACITY_RELATIVE_TO_FULL</a>.</td></tr><tr><td>capacity_remaining</td><td><code>float</code></td><td>Ah</td><td>invalid:NaN</td><td>Remaining charge (until empty). NaN: field not provided. Note: If <a href="#MAV_BATTERY_STATUS_FLAGS_CAPACITY_RELATIVE_TO_FULL">MAV_BATTERY_STATUS_FLAGS_CAPACITY_RELATIVE_TO_FULL</a> is unset, this value is based on the assumption the battery was full when the system was powered.</td></tr><tr><td>percent_remaining</td><td><code>uint8_t</code></td><td>%</td><td>invalid:UINT8_MAX</td><td>Remaining battery energy. Values: [0-100], UINT8_MAX: field not provided.</td></tr><tr><td>status_flags</td><td><code>uint32_t</code></td><td></td><td><a href="#MAV_BATTERY_STATUS_FLAGS">MAV_BATTERY_STATUS_FLAGS</a></td><td>Fault, health, readiness, and other status indications.</td></tr></tbody></table><h3 id="GROUP_START" tabindex="-1">GROUP_START (414) — [WIP] <a class="header-anchor" href="#GROUP_START" aria-label="Permalink to &quot;GROUP_START (414) — [WIP] {#GROUP_START}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Emitted during mission execution when control reaches <a href="#MAV_CMD_GROUP_START">MAV_CMD_GROUP_START</a>.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>描述</th></tr></thead><tbody><tr><td>group_id</td><td><code>uint32_t</code></td><td></td><td>Mission-unique group id (from <a href="#MAV_CMD_GROUP_START">MAV_CMD_GROUP_START</a>).</td></tr><tr><td>mission_checksum</td><td><code>uint32_t</code></td><td></td><td>CRC32 checksum of current plan for <a href="#MAV_MISSION_TYPE_ALL">MAV_MISSION_TYPE_ALL</a>. As defined in <a href="#MISSION_CHECKSUM">MISSION_CHECKSUM</a> message.</td></tr><tr><td>time_usec</td><td><code>uint64_t</code></td><td>us</td><td>Timestamp (UNIX Epoch time or time since system boot).<br>The receiving end can infer timestamp format (since 1.1.1970 or since system boot) by checking for the magnitude of the number.</td></tr></tbody></table><h3 id="GROUP_END" tabindex="-1">GROUP_END (415) — [WIP] <a class="header-anchor" href="#GROUP_END" aria-label="Permalink to &quot;GROUP_END (415) — [WIP] {#GROUP_END}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Emitted during mission execution when control reaches <a href="#MAV_CMD_GROUP_END">MAV_CMD_GROUP_END</a>.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>描述</th></tr></thead><tbody><tr><td>group_id</td><td><code>uint32_t</code></td><td></td><td>Mission-unique group id (from <a href="#MAV_CMD_GROUP_END">MAV_CMD_GROUP_END</a>).</td></tr><tr><td>mission_checksum</td><td><code>uint32_t</code></td><td></td><td>CRC32 checksum of current plan for <a href="#MAV_MISSION_TYPE_ALL">MAV_MISSION_TYPE_ALL</a>. As defined in <a href="#MISSION_CHECKSUM">MISSION_CHECKSUM</a> message.</td></tr><tr><td>time_usec</td><td><code>uint64_t</code></td><td>us</td><td>Timestamp (UNIX Epoch time or time since system boot).<br>The receiving end can infer timestamp format (since 1.1.1970 or since system boot) by checking for the magnitude of the number.</td></tr></tbody></table><h3 id="RADIO_RC_CHANNELS" tabindex="-1">RADIO_RC_CHANNELS (420) — [WIP] <a class="header-anchor" href="#RADIO_RC_CHANNELS" aria-label="Permalink to &quot;RADIO_RC_CHANNELS (420) — [WIP] {#RADIO_RC_CHANNELS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>RC channel outputs from a MAVLink RC receiver for input to a flight controller or other components (allows an RC receiver to connect via MAVLink instead of some other protocol such as PPM-Sum or S.BUS).</p><p>Note that this is not intended to be an over-the-air format, and does not replace <a href="#RC_CHANNELS">RC_CHANNELS</a> and similar messages reported by the flight controller. The target_system field should normally be set to the system id of the system to control, typically the flight controller. The target_component field can normally be set to 0, so that all components of the system can receive the message. The channels array field can publish up to 32 channels; the number of channel items used in the array is specified in the count field. The time_last_update_ms field contains the timestamp of the last received valid channels data in the receiver&#39;s time domain. The count field indicates the first index of the channel array that is not used for channel data (this and later indexes are zero-filled). The <a href="#RADIO_RC_CHANNELS_FLAGS_OUTDATED">RADIO_RC_CHANNELS_FLAGS_OUTDATED</a> flag is set by the receiver if the channels data is not up-to-date (for example, if new data from the transmitter could not be validated so the last valid data is resent). The <a href="#RADIO_RC_CHANNELS_FLAGS_FAILSAFE">RADIO_RC_CHANNELS_FLAGS_FAILSAFE</a> failsafe flag is set by the receiver if the receiver&#39;s failsafe condition is met (implementation dependent, e.g., connection to the RC radio is lost). In this case time_last_update_ms still contains the timestamp of the last valid channels data, but the content of the channels data is not defined by the protocol (it is up to the implementation of the receiver). For instance, the channels data could contain failsafe values configured in the receiver; the default is to carry the last valid data. Note: The RC channels fields are extensions to ensure that they are located at the end of the serialized payload and subject to MAVLink&#39;s trailing-zero trimming.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>target_system</td><td><code>uint8_t</code></td><td></td><td></td><td>System ID (ID of target system, normally flight controller).</td></tr><tr><td>target_component</td><td><code>uint8_t</code></td><td></td><td></td><td>Component ID (normally 0 for broadcast).</td></tr><tr><td>time_last_update_ms</td><td><code>uint32_t</code></td><td>ms</td><td></td><td>Time when the data in the channels field were last updated (time since boot in the receiver&#39;s time domain).</td></tr><tr><td>flags</td><td><code>uint16_t</code></td><td></td><td><a href="#RADIO_RC_CHANNELS_FLAGS">RADIO_RC_CHANNELS_FLAGS</a></td><td>Radio RC channels status flags.</td></tr><tr><td>count</td><td><code>uint8_t</code></td><td></td><td></td><td>Total number of RC channels being received. This can be larger than 32, indicating that more channels are available but not given in this message.</td></tr><tr><td><span class="ext">channels</span> <a href="#mav2_extension_field">++</a></td><td><code>int16_t[32]</code></td><td></td><td>min:-4096 max:4096</td><td>RC channels.<br>Channel values are in centered 13 bit format. Range is -4096 to 4096, center is 0. Conversion to PWM is x * 5/32 + 1500.<br>Channels with indexes equal or above count should be set to 0, to benefit from MAVLink&#39;s trailing-zero trimming.</td></tr></tbody></table><h3 id="AVAILABLE_MODES" tabindex="-1">AVAILABLE_MODES (435) — [WIP] <a class="header-anchor" href="#AVAILABLE_MODES" aria-label="Permalink to &quot;AVAILABLE_MODES (435) — [WIP] {#AVAILABLE_MODES}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Get information about a particular flight modes.</p><p>The message can be enumerated or requested for a particular mode using <a href="#MAV_CMD_REQUEST_MESSAGE">MAV_CMD_REQUEST_MESSAGE</a>. Specify 0 in param2 to request that the message is emitted for all available modes or the specific index for just one mode. The modes must be available/settable for the current vehicle/frame type. Each modes should only be emitted once (even if it is both standard and custom).</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>number_modes</td><td><code>uint8_t</code></td><td></td><td>The total number of available modes for the current vehicle type.</td></tr><tr><td>mode_index</td><td><code>uint8_t</code></td><td></td><td>The current mode index within number_modes, indexed from 1.</td></tr><tr><td>standard_mode</td><td><code>uint8_t</code></td><td><a href="#MAV_STANDARD_MODE">MAV_STANDARD_MODE</a></td><td>Standard mode.</td></tr><tr><td>custom_mode</td><td><code>uint32_t</code></td><td></td><td>A bitfield for use for autopilot-specific flags</td></tr><tr><td>properties</td><td><code>uint32_t</code></td><td><a href="#MAV_MODE_PROPERTY">MAV_MODE_PROPERTY</a></td><td>Mode properties.</td></tr><tr><td>mode_name</td><td><code>char[35]</code></td><td></td><td>Name of custom mode, with null termination character. Should be omitted for standard modes.</td></tr></tbody></table><h3 id="CURRENT_MODE" tabindex="-1">CURRENT_MODE (436) — [WIP] <a class="header-anchor" href="#CURRENT_MODE" aria-label="Permalink to &quot;CURRENT_MODE (436) — [WIP] {#CURRENT_MODE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Get the current mode.</p><p>This should be emitted on any mode change, and broadcast at low rate (nominally 0.5 Hz). It may be requested using <a href="#MAV_CMD_REQUEST_MESSAGE">MAV_CMD_REQUEST_MESSAGE</a>.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>standard_mode</td><td><code>uint8_t</code></td><td><a href="#MAV_STANDARD_MODE">MAV_STANDARD_MODE</a></td><td>Standard mode.</td></tr><tr><td>custom_mode</td><td><code>uint32_t</code></td><td></td><td>A bitfield for use for autopilot-specific flags</td></tr><tr><td>intended_custom_mode</td><td><code>uint32_t</code></td><td>invalid:0</td><td>The custom_mode of the mode that was last commanded by the user (for example, with <a href="#MAV_CMD_DO_SET_STANDARD_MODE">MAV_CMD_DO_SET_STANDARD_MODE</a>, <a href="#MAV_CMD_DO_SET_MODE">MAV_CMD_DO_SET_MODE</a> or via RC). This should usually be the same as custom_mode. It will be different if the vehicle is unable to enter the intended mode, or has left that mode due to a failsafe condition. 0 indicates the intended custom mode is unknown/not supplied</td></tr></tbody></table><h3 id="AVAILABLE_MODES_MONITOR" tabindex="-1">AVAILABLE_MODES_MONITOR (437) — [WIP] <a class="header-anchor" href="#AVAILABLE_MODES_MONITOR" aria-label="Permalink to &quot;AVAILABLE_MODES_MONITOR (437) — [WIP] {#AVAILABLE_MODES_MONITOR}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>A change to the sequence number indicates that the set of <a href="#AVAILABLE_MODES">AVAILABLE_MODES</a> has changed.</p><p>A receiver must re-request all available modes whenever the sequence number changes. This is only emitted after the first change and should then be broadcast at low rate (nominally 0.3 Hz) and on change.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>描述</th></tr></thead><tbody><tr><td>seq</td><td><code>uint8_t</code></td><td>Sequence number. The value iterates sequentially whenever <a href="#AVAILABLE_MODES">AVAILABLE_MODES</a> changes (e.g. support for a new mode is added/removed dynamically).</td></tr></tbody></table><h3 id="GNSS_INTEGRITY" tabindex="-1">GNSS_INTEGRITY (441) — [WIP] <a class="header-anchor" href="#GNSS_INTEGRITY" aria-label="Permalink to &quot;GNSS_INTEGRITY (441) — [WIP] {#GNSS_INTEGRITY}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Information about key components of GNSS receivers, like signal authentication, interference and system errors.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>id</td><td><code>uint8_t</code></td><td></td><td></td><td>GNSS receiver id. Must match instance ids of other messages from same receiver.<br>Messages with same value are from the same source (instance).</td></tr><tr><td>system_errors</td><td><code>uint32_t</code></td><td></td><td><a href="#GPS_SYSTEM_ERROR_FLAGS">GPS_SYSTEM_ERROR_FLAGS</a></td><td>Errors in the GPS system.</td></tr><tr><td>authentication_state</td><td><code>uint8_t</code></td><td></td><td><a href="#GPS_AUTHENTICATION_STATE">GPS_AUTHENTICATION_STATE</a></td><td>Signal authentication state of the GPS system.</td></tr><tr><td>jamming_state</td><td><code>uint8_t</code></td><td></td><td><a href="#GPS_JAMMING_STATE">GPS_JAMMING_STATE</a></td><td>Signal jamming state of the GPS system.</td></tr><tr><td>spoofing_state</td><td><code>uint8_t</code></td><td></td><td><a href="#GPS_SPOOFING_STATE">GPS_SPOOFING_STATE</a></td><td>Signal spoofing state of the GPS system.</td></tr><tr><td>raim_state</td><td><code>uint8_t</code></td><td></td><td><a href="#GPS_RAIM_STATE">GPS_RAIM_STATE</a></td><td>The state of the RAIM processing.</td></tr><tr><td>raim_hfom</td><td><code>uint16_t</code></td><td>厘米</td><td>invalid:UINT16_MAX</td><td>Horizontal expected accuracy using satellites successfully validated using RAIM.</td></tr><tr><td>raim_vfom</td><td><code>uint16_t</code></td><td>厘米</td><td>invalid:UINT16_MAX</td><td>Vertical expected accuracy using satellites successfully validated using RAIM.</td></tr><tr><td>corrections_quality</td><td><code>uint8_t</code></td><td></td><td>invalid:UINT8_MAX min:0 max:10</td><td>An abstract value representing the estimated quality of incoming corrections, or 255 if not available.</td></tr><tr><td>system_status_summary</td><td><code>uint8_t</code></td><td></td><td>invalid:UINT8_MAX min:0 max:10</td><td>An abstract value representing the overall status of the receiver, or 255 if not available.</td></tr><tr><td>gnss_signal_quality</td><td><code>uint8_t</code></td><td></td><td>invalid:UINT8_MAX min:0 max:10</td><td>An abstract value representing the quality of incoming GNSS signals, or 255 if not available.</td></tr><tr><td>post_processing_quality</td><td><code>uint8_t</code></td><td></td><td>invalid:UINT8_MAX min:0 max:10</td><td>An abstract value representing the estimated PPK quality, or 255 if not available.</td></tr></tbody></table><h3 id="TARGET_ABSOLUTE" tabindex="-1">TARGET_ABSOLUTE (510) — [WIP] <a class="header-anchor" href="#TARGET_ABSOLUTE" aria-label="Permalink to &quot;TARGET_ABSOLUTE (510) — [WIP] {#TARGET_ABSOLUTE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Current motion information from sensors on a target</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>timestamp</td><td><code>uint64_t</code></td><td>us</td><td></td><td>Timestamp (UNIX epoch time).</td></tr><tr><td>id</td><td><code>uint8_t</code></td><td></td><td></td><td>The ID of the target if multiple targets are present</td></tr><tr><td>sensor_capabilities</td><td><code>uint8_t</code></td><td></td><td><a href="#TARGET_ABSOLUTE_SENSOR_CAPABILITY_FLAGS">TARGET_ABSOLUTE_SENSOR_CAPABILITY_FLAGS</a></td><td>Bitmap to indicate the sensor&#39;s reporting capabilities</td></tr><tr><td>lat</td><td><code>int32_t</code></td><td>degE7</td><td></td><td>Target&#39;s latitude (WGS84)</td></tr><tr><td>lon</td><td><code>int32_t</code></td><td>degE7</td><td></td><td>Target&#39;s longitude (WGS84)</td></tr><tr><td>alt</td><td><code>float</code></td><td>m</td><td></td><td>Target&#39;s altitude (AMSL)</td></tr><tr><td>vel</td><td><code>float[3]</code></td><td>m/s</td><td>invalid:[0]</td><td>Target&#39;s velocity in its body frame</td></tr><tr><td>acc</td><td><code>float[3]</code></td><td>m/s/s</td><td>invalid:[0]</td><td>Linear target&#39;s acceleration in its body frame</td></tr><tr><td>q_target</td><td><code>float[4]</code></td><td></td><td>invalid:[0]</td><td>Quaternion of the target&#39;s orientation from its body frame to the vehicle&#39;s NED frame.</td></tr><tr><td>rates</td><td><code>float[3]</code></td><td>rad/s</td><td>invalid:[0]</td><td>Target&#39;s roll, pitch and yaw rates</td></tr><tr><td>position_std</td><td><code>float[2]</code></td><td>m</td><td></td><td>Standard deviation of horizontal (eph) and vertical (epv) position errors</td></tr><tr><td>vel_std</td><td><code>float[3]</code></td><td>m/s</td><td></td><td>Standard deviation of the target&#39;s velocity in its body frame</td></tr><tr><td>acc_std</td><td><code>float[3]</code></td><td>m/s/s</td><td></td><td>Standard deviation of the target&#39;s acceleration in its body frame</td></tr></tbody></table><h3 id="TARGET_RELATIVE" tabindex="-1">TARGET_RELATIVE (511) — [WIP] <a class="header-anchor" href="#TARGET_RELATIVE" aria-label="Permalink to &quot;TARGET_RELATIVE (511) — [WIP] {#TARGET_RELATIVE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>The location of a target measured by MAV&#39;s onboard sensors.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>Units</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>timestamp</td><td><code>uint64_t</code></td><td>us</td><td></td><td>Timestamp (UNIX epoch time)</td></tr><tr><td>id</td><td><code>uint8_t</code></td><td></td><td></td><td>The ID of the target if multiple targets are present<br>Messages with same value are from the same source (instance).</td></tr><tr><td>frame</td><td><code>uint8_t</code></td><td></td><td><a href="#TARGET_OBS_FRAME">TARGET_OBS_FRAME</a></td><td>Coordinate frame used for following fields.</td></tr><tr><td>x</td><td><code>float</code></td><td>m</td><td></td><td>X Position of the target in <a href="#TARGET_OBS_FRAME">TARGET_OBS_FRAME</a></td></tr><tr><td>y</td><td><code>float</code></td><td>m</td><td></td><td>Y Position of the target in <a href="#TARGET_OBS_FRAME">TARGET_OBS_FRAME</a></td></tr><tr><td>z</td><td><code>float</code></td><td>m</td><td></td><td>Z Position of the target in <a href="#TARGET_OBS_FRAME">TARGET_OBS_FRAME</a></td></tr><tr><td>pos_std</td><td><code>float[3]</code></td><td>m</td><td></td><td>Standard deviation of the target&#39;s position in <a href="#TARGET_OBS_FRAME">TARGET_OBS_FRAME</a></td></tr><tr><td>yaw_std</td><td><code>float</code></td><td>rad</td><td></td><td>Standard deviation of the target&#39;s orientation in <a href="#TARGET_OBS_FRAME">TARGET_OBS_FRAME</a></td></tr><tr><td>q_target</td><td><code>float[4]</code></td><td></td><td></td><td>Quaternion of the target&#39;s orientation from the target&#39;s frame to the <a href="#TARGET_OBS_FRAME">TARGET_OBS_FRAME</a> (w, x, y, z order, zero-rotation is 1, 0, 0, 0)</td></tr><tr><td>q_sensor</td><td><code>float[4]</code></td><td></td><td></td><td>Quaternion of the sensor&#39;s orientation from <a href="#TARGET_OBS_FRAME">TARGET_OBS_FRAME</a> to vehicle-carried NED. (Ignored if set to (0,0,0,0)) (w, x, y, z order, zero-rotation is 1, 0, 0, 0)</td></tr><tr><td>type</td><td><code>uint8_t</code></td><td></td><td><a href="#LANDING_TARGET_TYPE">LANDING_TARGET_TYPE</a></td><td>Type of target</td></tr></tbody></table><h3 id="CONTROL_STATUS" tabindex="-1">CONTROL_STATUS (512) — [WIP] <a class="header-anchor" href="#CONTROL_STATUS" aria-label="Permalink to &quot;CONTROL_STATUS (512) — [WIP] {#CONTROL_STATUS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Information about GCS in control of this MAV. This should be broadcast at low rate (nominally 1 Hz) and emitted when ownership or takeover status change. Control over MAV is requested using <a href="#MAV_CMD_REQUEST_OPERATOR_CONTROL">MAV_CMD_REQUEST_OPERATOR_CONTROL</a>.</p><table tabindex="0"><thead><tr><th>Field Name</th><th>Type</th><th>值</th><th>描述</th></tr></thead><tbody><tr><td>sysid_in_control</td><td><code>uint8_t</code></td><td></td><td>System ID of GCS MAVLink component in control (0: no GCS in control).</td></tr><tr><td>flags</td><td><code>uint8_t</code></td><td><a href="#GCS_CONTROL_STATUS_FLAGS">GCS_CONTROL_STATUS_FLAGS</a></td><td>Control status. For example, whether takeover is allowed, and whether this message instance defines the default controlling GCS for the whole system.</td></tr></tbody></table><h2 id="enumerated-types" tabindex="-1">Enumerated Types <a class="header-anchor" href="#enumerated-types" aria-label="Permalink to &quot;Enumerated Types&quot;">​</a></h2><h3 id="AIRSPEED_SENSOR_FLAGS" tabindex="-1">AIRSPEED_SENSOR_FLAGS — [WIP] <a class="header-anchor" href="#AIRSPEED_SENSOR_FLAGS" aria-label="Permalink to &quot;AIRSPEED_SENSOR_FLAGS — [WIP] {#AIRSPEED_SENSOR_FLAGS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>(Bitmask) Airspeed sensor flags</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="AIRSPEED_SENSOR_UNHEALTHY"></a>0</td><td><a href="#AIRSPEED_SENSOR_UNHEALTHY">AIRSPEED_SENSOR_UNHEALTHY</a></td><td>Airspeed sensor is unhealthy</td></tr><tr><td><a id="AIRSPEED_SENSOR_USING"></a>1</td><td><a href="#AIRSPEED_SENSOR_USING">AIRSPEED_SENSOR_USING</a></td><td>True if the data from this sensor is being actively used by the flight controller for guidance, navigation or control.</td></tr></tbody></table><h3 id="PARAM_TRANSACTION_TRANSPORT" tabindex="-1">PARAM_TRANSACTION_TRANSPORT — [WIP] <a class="header-anchor" href="#PARAM_TRANSACTION_TRANSPORT" aria-label="Permalink to &quot;PARAM_TRANSACTION_TRANSPORT — [WIP] {#PARAM_TRANSACTION_TRANSPORT}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Possible transport layers to set and get parameters via mavlink during a parameter transaction.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="PARAM_TRANSACTION_TRANSPORT_PARAM"></a>0</td><td><a href="#PARAM_TRANSACTION_TRANSPORT_PARAM">PARAM_TRANSACTION_TRANSPORT_PARAM</a></td><td>Transaction over param transport.</td></tr><tr><td><a id="PARAM_TRANSACTION_TRANSPORT_PARAM_EXT"></a>1</td><td><a href="#PARAM_TRANSACTION_TRANSPORT_PARAM_EXT">PARAM_TRANSACTION_TRANSPORT_PARAM_EXT</a></td><td>Transaction over param_ext transport.</td></tr></tbody></table><h3 id="PARAM_TRANSACTION_ACTION" tabindex="-1">PARAM_TRANSACTION_ACTION — [WIP] <a class="header-anchor" href="#PARAM_TRANSACTION_ACTION" aria-label="Permalink to &quot;PARAM_TRANSACTION_ACTION — [WIP] {#PARAM_TRANSACTION_ACTION}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Possible parameter transaction actions.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="PARAM_TRANSACTION_ACTION_START"></a>0</td><td><a href="#PARAM_TRANSACTION_ACTION_START">PARAM_TRANSACTION_ACTION_START</a></td><td>Commit the current parameter transaction.</td></tr><tr><td><a id="PARAM_TRANSACTION_ACTION_COMMIT"></a>1</td><td><a href="#PARAM_TRANSACTION_ACTION_COMMIT">PARAM_TRANSACTION_ACTION_COMMIT</a></td><td>Commit the current parameter transaction.</td></tr><tr><td><a id="PARAM_TRANSACTION_ACTION_CANCEL"></a>2</td><td><a href="#PARAM_TRANSACTION_ACTION_CANCEL">PARAM_TRANSACTION_ACTION_CANCEL</a></td><td>Cancel the current parameter transaction.</td></tr></tbody></table><h3 id="MAV_STANDARD_MODE" tabindex="-1">MAV_STANDARD_MODE — [WIP] <a class="header-anchor" href="#MAV_STANDARD_MODE" aria-label="Permalink to &quot;MAV_STANDARD_MODE — [WIP] {#MAV_STANDARD_MODE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Standard modes with a well understood meaning across flight stacks and vehicle types.</p><p>For example, most flight stack have the concept of a &quot;return&quot; or &quot;RTL&quot; mode that takes a vehicle to safety, even though the precise mechanics of this mode may differ. Modes may be set using <a href="#MAV_CMD_DO_SET_STANDARD_MODE">MAV_CMD_DO_SET_STANDARD_MODE</a>.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="MAV_STANDARD_MODE_NON_STANDARD"></a>0</td><td><a href="#MAV_STANDARD_MODE_NON_STANDARD">MAV_STANDARD_MODE_NON_STANDARD</a></td><td>Non standard mode.<br>This may be used when reporting the mode if the current flight mode is not a standard mode.</td></tr><tr><td><a id="MAV_STANDARD_MODE_POSITION_HOLD"></a>1</td><td><a href="#MAV_STANDARD_MODE_POSITION_HOLD">MAV_STANDARD_MODE_POSITION_HOLD</a></td><td>Position mode (manual).<br>Position-controlled and stabilized manual mode.<br>When sticks are released vehicles return to their level-flight orientation and hold both position and altitude against wind and external forces.<br>This mode can only be set by vehicles that can hold a fixed position.<br>Multicopter (MC) vehicles actively brake and hold both position and altitude against wind and external forces.<br>Hybrid MC/FW (&quot;VTOL&quot;) vehicles first transition to multicopter mode (if needed) but otherwise behave in the same way as MC vehicles.<br>Fixed-wing (FW) vehicles must not support this mode.<br>Other vehicle types must not support this mode (this may be revisited through the PR process).</td></tr><tr><td><a id="MAV_STANDARD_MODE_ORBIT"></a>2</td><td><a href="#MAV_STANDARD_MODE_ORBIT">MAV_STANDARD_MODE_ORBIT</a></td><td>Orbit (manual).<br>Position-controlled and stabilized manual mode.<br>The vehicle circles around a fixed setpoint in the horizontal plane at a particular radius, altitude, and direction.<br>Flight stacks may further allow manual control over the setpoint position, radius, direction, speed, and/or altitude of the circle, but this is not mandated.<br>Flight stacks may support the <a href="https://mavlink.io/en/messages/common.html#MAV_CMD_DO_ORBIT" target="_blank" rel="noreferrer">MAV_CMD_DO_ORBIT</a> for changing the orbit parameters.<br>MC and FW vehicles may support this mode.<br>Hybrid MC/FW (&quot;VTOL&quot;) vehicles may support this mode in MC/FW or both modes; if the mode is not supported by the current configuration the vehicle should transition to the supported configuration.<br>Other vehicle types must not support this mode (this may be revisited through the PR process).</td></tr><tr><td><a id="MAV_STANDARD_MODE_CRUISE"></a>3</td><td><a href="#MAV_STANDARD_MODE_CRUISE">MAV_STANDARD_MODE_CRUISE</a></td><td>Cruise mode (manual).<br>Position-controlled and stabilized manual mode.<br>When sticks are released vehicles return to their level-flight orientation and hold their original track against wind and external forces.<br>Fixed-wing (FW) vehicles level orientation and maintain current track and altitude against wind and external forces.<br>Hybrid MC/FW (&quot;VTOL&quot;) vehicles first transition to FW mode (if needed) but otherwise behave in the same way as MC vehicles.<br>Multicopter (MC) vehicles must not support this mode.<br>Other vehicle types must not support this mode (this may be revisited through the PR process).</td></tr><tr><td><a id="MAV_STANDARD_MODE_ALTITUDE_HOLD"></a>4</td><td><a href="#MAV_STANDARD_MODE_ALTITUDE_HOLD">MAV_STANDARD_MODE_ALTITUDE_HOLD</a></td><td>Altitude hold (manual).<br>Altitude-controlled and stabilized manual mode.<br>When sticks are released vehicles return to their level-flight orientation and hold their altitude.<br>MC vehicles continue with existing momentum and may move with wind (or other external forces).<br>FW vehicles continue with current heading, but may be moved off-track by wind.<br>Hybrid MC/FW (&quot;VTOL&quot;) vehicles behave according to their current configuration/mode (FW or MC).<br>Other vehicle types must not support this mode (this may be revisited through the PR process).</td></tr><tr><td><a id="MAV_STANDARD_MODE_RETURN_HOME"></a>5</td><td><a href="#MAV_STANDARD_MODE_RETURN_HOME">MAV_STANDARD_MODE_RETURN_HOME</a></td><td>Return home mode (auto).<br>Automatic mode that returns vehicle to home via a safe flight path.<br>It may also automatically land the vehicle (i.e. RTL).<br>The precise flight path and landing behaviour depend on vehicle configuration and type.</td></tr><tr><td><a id="MAV_STANDARD_MODE_SAFE_RECOVERY"></a>6</td><td><a href="#MAV_STANDARD_MODE_SAFE_RECOVERY">MAV_STANDARD_MODE_SAFE_RECOVERY</a></td><td>Safe recovery mode (auto).<br>Automatic mode that takes vehicle to a predefined safe location via a safe flight path (rally point or mission defined landing) .<br>It may also automatically land the vehicle.<br>The precise return location, flight path, and landing behaviour depend on vehicle configuration and type.</td></tr><tr><td><a id="MAV_STANDARD_MODE_MISSION"></a>7</td><td><a href="#MAV_STANDARD_MODE_MISSION">MAV_STANDARD_MODE_MISSION</a></td><td>Mission mode (automatic).<br>Automatic mode that executes MAVLink missions.<br>Missions are executed from the current waypoint as soon as the mode is enabled.</td></tr><tr><td><a id="MAV_STANDARD_MODE_LAND"></a>8</td><td><a href="#MAV_STANDARD_MODE_LAND">MAV_STANDARD_MODE_LAND</a></td><td>Land mode (auto).<br>Automatic mode that lands the vehicle at the current location.<br>The precise landing behaviour depends on vehicle configuration and type.</td></tr><tr><td><a id="MAV_STANDARD_MODE_TAKEOFF"></a>9</td><td><a href="#MAV_STANDARD_MODE_TAKEOFF">MAV_STANDARD_MODE_TAKEOFF</a></td><td>Takeoff mode (auto).<br>Automatic takeoff mode.<br>The precise takeoff behaviour depends on vehicle configuration and type.</td></tr></tbody></table><h3 id="MAV_MODE_PROPERTY" tabindex="-1">MAV_MODE_PROPERTY — [WIP] <a class="header-anchor" href="#MAV_MODE_PROPERTY" aria-label="Permalink to &quot;MAV_MODE_PROPERTY — [WIP] {#MAV_MODE_PROPERTY}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>(Bitmask) Mode properties.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="MAV_MODE_PROPERTY_ADVANCED"></a>1</td><td><a href="#MAV_MODE_PROPERTY_ADVANCED">MAV_MODE_PROPERTY_ADVANCED</a></td><td>If set, this mode is an advanced mode.<br>For example a rate-controlled manual mode might be advanced, whereas a position-controlled manual mode is not.<br>A GCS can optionally use this flag to configure the UI for its intended users.</td></tr><tr><td><a id="MAV_MODE_PROPERTY_NOT_USER_SELECTABLE"></a>2</td><td><a href="#MAV_MODE_PROPERTY_NOT_USER_SELECTABLE">MAV_MODE_PROPERTY_NOT_USER_SELECTABLE</a></td><td>If set, this mode should not be added to the list of selectable modes.<br>The mode might still be selected by the FC directly (for example as part of a failsafe).</td></tr></tbody></table><h3 id="MAV_BATTERY_STATUS_FLAGS" tabindex="-1">MAV_BATTERY_STATUS_FLAGS — [WIP] <a class="header-anchor" href="#MAV_BATTERY_STATUS_FLAGS" aria-label="Permalink to &quot;MAV_BATTERY_STATUS_FLAGS — [WIP] {#MAV_BATTERY_STATUS_FLAGS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>(Bitmask) Battery status flags for fault, health and state indication.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_NOT_READY_TO_USE"></a>1</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_NOT_READY_TO_USE">MAV_BATTERY_STATUS_FLAGS_NOT_READY_TO_USE</a></td><td>The battery is not ready to use (fly).<br>Set if the battery has faults or other conditions that make it unsafe to fly with.<br>Note: It will be the logical OR of other status bits (chosen by the manufacturer/integrator).</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_CHARGING"></a>2</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_CHARGING">MAV_BATTERY_STATUS_FLAGS_CHARGING</a></td><td>Battery is charging.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_CELL_BALANCING"></a>4</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_CELL_BALANCING">MAV_BATTERY_STATUS_FLAGS_CELL_BALANCING</a></td><td>Battery is cell balancing (during charging).<br>Not ready to use (<a href="#MAV_BATTERY_STATUS_FLAGS_NOT_READY_TO_USE">MAV_BATTERY_STATUS_FLAGS_NOT_READY_TO_USE</a> may be set).</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_CELL_IMBALANCE"></a>8</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_CELL_IMBALANCE">MAV_BATTERY_STATUS_FLAGS_FAULT_CELL_IMBALANCE</a></td><td>Battery cells are not balanced.<br>Not ready to use.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_AUTO_DISCHARGING"></a>16</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_AUTO_DISCHARGING">MAV_BATTERY_STATUS_FLAGS_AUTO_DISCHARGING</a></td><td>Battery is auto discharging (towards storage level).<br>Not ready to use (<a href="#MAV_BATTERY_STATUS_FLAGS_NOT_READY_TO_USE">MAV_BATTERY_STATUS_FLAGS_NOT_READY_TO_USE</a> would be set).</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_REQUIRES_SERVICE"></a>32</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_REQUIRES_SERVICE">MAV_BATTERY_STATUS_FLAGS_REQUIRES_SERVICE</a></td><td>Battery requires service (not safe to fly).<br>This is set at vendor discretion.<br>It is likely to be set for most faults, and may also be set according to a maintenance schedule (such as age, or number of recharge cycles, etc.).</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_BAD_BATTERY"></a>64</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_BAD_BATTERY">MAV_BATTERY_STATUS_FLAGS_BAD_BATTERY</a></td><td>Battery is faulty and cannot be repaired (not safe to fly).<br>This is set at vendor discretion.<br>The battery should be disposed of safely.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_PROTECTIONS_ENABLED"></a>128</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_PROTECTIONS_ENABLED">MAV_BATTERY_STATUS_FLAGS_PROTECTIONS_ENABLED</a></td><td>Automatic battery protection monitoring is enabled.<br>When enabled, the system will monitor for certain kinds of faults, such as cells being over-voltage.<br>If a fault is triggered then and protections are enabled then a safety fault (<a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_PROTECTION_SYSTEM">MAV_BATTERY_STATUS_FLAGS_FAULT_PROTECTION_SYSTEM</a>) will be set and power from the battery will be stopped.<br>Note that battery protection monitoring should only be enabled when the vehicle is landed. Once the vehicle is armed, or starts moving, the protections should be disabled to prevent false positives from disabling the output.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_PROTECTION_SYSTEM"></a>256</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_PROTECTION_SYSTEM">MAV_BATTERY_STATUS_FLAGS_FAULT_PROTECTION_SYSTEM</a></td><td>The battery fault protection system had detected a fault and cut all power from the battery.<br>This will only trigger if <a href="#MAV_BATTERY_STATUS_FLAGS_PROTECTIONS_ENABLED">MAV_BATTERY_STATUS_FLAGS_PROTECTIONS_ENABLED</a> is set.<br>Other faults like <a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_VOLT">MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_VOLT</a> may also be set, indicating the cause of the protection fault.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_VOLT"></a>512</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_VOLT">MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_VOLT</a></td><td>One or more cells are above their maximum voltage rating.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_UNDER_VOLT"></a>1024</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_UNDER_VOLT">MAV_BATTERY_STATUS_FLAGS_FAULT_UNDER_VOLT</a></td><td>One or more cells are below their minimum voltage rating.<br>A battery that had deep-discharged might be irrepairably damaged, and set both <a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_UNDER_VOLT">MAV_BATTERY_STATUS_FLAGS_FAULT_UNDER_VOLT</a> and <a href="#MAV_BATTERY_STATUS_FLAGS_BAD_BATTERY">MAV_BATTERY_STATUS_FLAGS_BAD_BATTERY</a>.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_TEMPERATURE"></a>2048</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_TEMPERATURE">MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_TEMPERATURE</a></td><td>Over-temperature fault.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_UNDER_TEMPERATURE"></a>4096</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_UNDER_TEMPERATURE">MAV_BATTERY_STATUS_FLAGS_FAULT_UNDER_TEMPERATURE</a></td><td>Under-temperature fault.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_CURRENT"></a>8192</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_CURRENT">MAV_BATTERY_STATUS_FLAGS_FAULT_OVER_CURRENT</a></td><td>Over-current fault.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_SHORT_CIRCUIT"></a>16384</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_SHORT_CIRCUIT">MAV_BATTERY_STATUS_FLAGS_FAULT_SHORT_CIRCUIT</a></td><td>Short circuit event detected.<br>The battery may or may not be safe to use (check other flags).</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_INCOMPATIBLE_VOLTAGE"></a>32768</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_INCOMPATIBLE_VOLTAGE">MAV_BATTERY_STATUS_FLAGS_FAULT_INCOMPATIBLE_VOLTAGE</a></td><td>Voltage not compatible with power rail voltage (batteries on same power rail should have similar voltage).</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_INCOMPATIBLE_FIRMWARE"></a>65536</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_INCOMPATIBLE_FIRMWARE">MAV_BATTERY_STATUS_FLAGS_FAULT_INCOMPATIBLE_FIRMWARE</a></td><td>Battery firmware is not compatible with current autopilot firmware.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_FAULT_INCOMPATIBLE_CELLS_CONFIGURATION"></a>131072</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_FAULT_INCOMPATIBLE_CELLS_CONFIGURATION">MAV_BATTERY_STATUS_FLAGS_FAULT_INCOMPATIBLE_CELLS_CONFIGURATION</a></td><td>Battery is not compatible due to cell configuration (e.g. 5s1p when vehicle requires 6s).</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_CAPACITY_RELATIVE_TO_FULL"></a>262144</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_CAPACITY_RELATIVE_TO_FULL">MAV_BATTERY_STATUS_FLAGS_CAPACITY_RELATIVE_TO_FULL</a></td><td>Battery capacity_consumed and capacity_remaining values are relative to a full battery (they sum to the total capacity of the battery).<br>This flag would be set for a smart battery that can accurately determine its remaining charge across vehicle reboots and discharge/recharge cycles.<br>If unset the capacity_consumed indicates the consumption since vehicle power-on, as measured using a power monitor. The capacity_remaining, if provided, indicates the estimated remaining capacity on the assumption that the battery was full on vehicle boot.<br>If unset a GCS is recommended to advise that users fully charge the battery on power on.</td></tr><tr><td><a id="MAV_BATTERY_STATUS_FLAGS_EXTENDED"></a>4294967295</td><td><a href="#MAV_BATTERY_STATUS_FLAGS_EXTENDED">MAV_BATTERY_STATUS_FLAGS_EXTENDED</a></td><td>Reserved (not used). If set, this will indicate that an additional status field exists for higher status values.</td></tr></tbody></table><h3 id="GCS_CONTROL_STATUS_FLAGS" tabindex="-1">GCS_CONTROL_STATUS_FLAGS — [WIP] <a class="header-anchor" href="#GCS_CONTROL_STATUS_FLAGS" aria-label="Permalink to &quot;GCS_CONTROL_STATUS_FLAGS — [WIP] {#GCS_CONTROL_STATUS_FLAGS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>(Bitmask) <a href="#CONTROL_STATUS">CONTROL_STATUS</a> flags.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="GCS_CONTROL_STATUS_FLAGS_SYSTEM_MANAGER"></a>1</td><td><a href="#GCS_CONTROL_STATUS_FLAGS_SYSTEM_MANAGER">GCS_CONTROL_STATUS_FLAGS_SYSTEM_MANAGER</a></td><td>If set, this <a href="#CONTROL_STATUS">CONTROL_STATUS</a> publishes the controlling GCS for the whole system. If unset, the <a href="#CONTROL_STATUS">CONTROL_STATUS</a> indicates the controlling GCS for just the component emitting the message. Note that to request control of the system a GCS should send <a href="#MAV_CMD_REQUEST_OPERATOR_CONTROL">MAV_CMD_REQUEST_OPERATOR_CONTROL</a> to the component emitting <a href="#CONTROL_STATUS">CONTROL_STATUS</a> with this flag set.</td></tr><tr><td><a id="GCS_CONTROL_STATUS_FLAGS_TAKEOVER_ALLOWED"></a>2</td><td><a href="#GCS_CONTROL_STATUS_FLAGS_TAKEOVER_ALLOWED">GCS_CONTROL_STATUS_FLAGS_TAKEOVER_ALLOWED</a></td><td>Takeover allowed (requests for control will be granted). If not set requests for control will be rejected, but the controlling GCS will be notified (and may release control or allow takeover).</td></tr></tbody></table><h3 id="TARGET_ABSOLUTE_SENSOR_CAPABILITY_FLAGS" tabindex="-1">TARGET_ABSOLUTE_SENSOR_CAPABILITY_FLAGS — [WIP] <a class="header-anchor" href="#TARGET_ABSOLUTE_SENSOR_CAPABILITY_FLAGS" aria-label="Permalink to &quot;TARGET_ABSOLUTE_SENSOR_CAPABILITY_FLAGS — [WIP] {#TARGET_ABSOLUTE_SENSOR_CAPABILITY_FLAGS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>(Bitmask) These flags indicate the sensor reporting capabilities for <a href="#TARGET_ABSOLUTE">TARGET_ABSOLUTE</a>.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="TARGET_ABSOLUTE_SENSOR_CAPABILITY_POSITION"></a>1</td><td><a href="#TARGET_ABSOLUTE_SENSOR_CAPABILITY_POSITION">TARGET_ABSOLUTE_SENSOR_CAPABILITY_POSITION</a></td><td></td></tr><tr><td><a id="TARGET_ABSOLUTE_SENSOR_CAPABILITY_VELOCITY"></a>2</td><td><a href="#TARGET_ABSOLUTE_SENSOR_CAPABILITY_VELOCITY">TARGET_ABSOLUTE_SENSOR_CAPABILITY_VELOCITY</a></td><td></td></tr><tr><td><a id="TARGET_ABSOLUTE_SENSOR_CAPABILITY_ACCELERATION"></a>4</td><td><a href="#TARGET_ABSOLUTE_SENSOR_CAPABILITY_ACCELERATION">TARGET_ABSOLUTE_SENSOR_CAPABILITY_ACCELERATION</a></td><td></td></tr><tr><td><a id="TARGET_ABSOLUTE_SENSOR_CAPABILITY_ATTITUDE"></a>8</td><td><a href="#TARGET_ABSOLUTE_SENSOR_CAPABILITY_ATTITUDE">TARGET_ABSOLUTE_SENSOR_CAPABILITY_ATTITUDE</a></td><td></td></tr><tr><td><a id="TARGET_ABSOLUTE_SENSOR_CAPABILITY_RATES"></a>16</td><td><a href="#TARGET_ABSOLUTE_SENSOR_CAPABILITY_RATES">TARGET_ABSOLUTE_SENSOR_CAPABILITY_RATES</a></td><td></td></tr></tbody></table><h3 id="TARGET_OBS_FRAME" tabindex="-1">TARGET_OBS_FRAME — [WIP] <a class="header-anchor" href="#TARGET_OBS_FRAME" aria-label="Permalink to &quot;TARGET_OBS_FRAME — [WIP] {#TARGET_OBS_FRAME}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>The frame of a target observation from an onboard sensor.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="TARGET_OBS_FRAME_LOCAL_NED"></a>0</td><td><a href="#TARGET_OBS_FRAME_LOCAL_NED">TARGET_OBS_FRAME_LOCAL_NED</a></td><td>NED local tangent frame (x: North, y: East, z: Down) with origin fixed relative to earth.</td></tr><tr><td><a id="TARGET_OBS_FRAME_BODY_FRD"></a>1</td><td><a href="#TARGET_OBS_FRAME_BODY_FRD">TARGET_OBS_FRAME_BODY_FRD</a></td><td>FRD local frame aligned to the vehicle&#39;s attitude (x: Forward, y: Right, z: Down) with an origin that travels with vehicle.</td></tr><tr><td><a id="TARGET_OBS_FRAME_LOCAL_OFFSET_NED"></a>2</td><td><a href="#TARGET_OBS_FRAME_LOCAL_OFFSET_NED">TARGET_OBS_FRAME_LOCAL_OFFSET_NED</a></td><td>NED local tangent frame (x: North, y: East, z: Down) with an origin that travels with vehicle.</td></tr><tr><td><a id="TARGET_OBS_FRAME_OTHER"></a>3</td><td><a href="#TARGET_OBS_FRAME_OTHER">TARGET_OBS_FRAME_OTHER</a></td><td>Other sensor frame for target observations neither in local NED nor in body FRD.</td></tr></tbody></table><h3 id="RADIO_RC_CHANNELS_FLAGS" tabindex="-1">RADIO_RC_CHANNELS_FLAGS — [WIP] <a class="header-anchor" href="#RADIO_RC_CHANNELS_FLAGS" aria-label="Permalink to &quot;RADIO_RC_CHANNELS_FLAGS — [WIP] {#RADIO_RC_CHANNELS_FLAGS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>(Bitmask) <a href="#RADIO_RC_CHANNELS">RADIO_RC_CHANNELS</a> flags (bitmask).</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="RADIO_RC_CHANNELS_FLAGS_FAILSAFE"></a>1</td><td><a href="#RADIO_RC_CHANNELS_FLAGS_FAILSAFE">RADIO_RC_CHANNELS_FLAGS_FAILSAFE</a></td><td>Failsafe is active. The content of the RC channels data in the <a href="#RADIO_RC_CHANNELS">RADIO_RC_CHANNELS</a> message is implementation dependent.</td></tr><tr><td><a id="RADIO_RC_CHANNELS_FLAGS_OUTDATED"></a>2</td><td><a href="#RADIO_RC_CHANNELS_FLAGS_OUTDATED">RADIO_RC_CHANNELS_FLAGS_OUTDATED</a></td><td>Channel data may be out of date. This is set when the receiver is unable to validate incoming data from the transmitter and has therefore resent the last valid data it received.</td></tr></tbody></table><h3 id="GPS_SYSTEM_ERROR_FLAGS" tabindex="-1">GPS_SYSTEM_ERROR_FLAGS — [WIP] <a class="header-anchor" href="#GPS_SYSTEM_ERROR_FLAGS" aria-label="Permalink to &quot;GPS_SYSTEM_ERROR_FLAGS — [WIP] {#GPS_SYSTEM_ERROR_FLAGS}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>(Bitmask) Flags indicating errors in a GPS receiver.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="GPS_SYSTEM_ERROR_INCOMING_CORRECTIONS"></a>1</td><td><a href="#GPS_SYSTEM_ERROR_INCOMING_CORRECTIONS">GPS_SYSTEM_ERROR_INCOMING_CORRECTIONS</a></td><td>There are problems with incoming correction streams.</td></tr><tr><td><a id="GPS_SYSTEM_ERROR_CONFIGURATION"></a>2</td><td><a href="#GPS_SYSTEM_ERROR_CONFIGURATION">GPS_SYSTEM_ERROR_CONFIGURATION</a></td><td>There are problems with the configuration.</td></tr><tr><td><a id="GPS_SYSTEM_ERROR_SOFTWARE"></a>4</td><td><a href="#GPS_SYSTEM_ERROR_SOFTWARE">GPS_SYSTEM_ERROR_SOFTWARE</a></td><td>There are problems with the software on the GPS receiver.</td></tr><tr><td><a id="GPS_SYSTEM_ERROR_ANTENNA"></a>8</td><td><a href="#GPS_SYSTEM_ERROR_ANTENNA">GPS_SYSTEM_ERROR_ANTENNA</a></td><td>There are problems with an antenna connected to the GPS receiver.</td></tr><tr><td><a id="GPS_SYSTEM_ERROR_EVENT_CONGESTION"></a>16</td><td><a href="#GPS_SYSTEM_ERROR_EVENT_CONGESTION">GPS_SYSTEM_ERROR_EVENT_CONGESTION</a></td><td>There are problems handling all incoming events.</td></tr><tr><td><a id="GPS_SYSTEM_ERROR_CPU_OVERLOAD"></a>32</td><td><a href="#GPS_SYSTEM_ERROR_CPU_OVERLOAD">GPS_SYSTEM_ERROR_CPU_OVERLOAD</a></td><td>The GPS receiver CPU is overloaded.</td></tr><tr><td><a id="GPS_SYSTEM_ERROR_OUTPUT_CONGESTION"></a>64</td><td><a href="#GPS_SYSTEM_ERROR_OUTPUT_CONGESTION">GPS_SYSTEM_ERROR_OUTPUT_CONGESTION</a></td><td>The GPS receiver is experiencing output congestion.</td></tr></tbody></table><h3 id="GPS_AUTHENTICATION_STATE" tabindex="-1">GPS_AUTHENTICATION_STATE — [WIP] <a class="header-anchor" href="#GPS_AUTHENTICATION_STATE" aria-label="Permalink to &quot;GPS_AUTHENTICATION_STATE — [WIP] {#GPS_AUTHENTICATION_STATE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Signal authentication state in a GPS receiver.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="GPS_AUTHENTICATION_STATE_UNKNOWN"></a>0</td><td><a href="#GPS_AUTHENTICATION_STATE_UNKNOWN">GPS_AUTHENTICATION_STATE_UNKNOWN</a></td><td>The GPS receiver does not provide GPS signal authentication info.</td></tr><tr><td><a id="GPS_AUTHENTICATION_STATE_INITIALIZING"></a>1</td><td><a href="#GPS_AUTHENTICATION_STATE_INITIALIZING">GPS_AUTHENTICATION_STATE_INITIALIZING</a></td><td>The GPS receiver is initializing signal authentication.</td></tr><tr><td><a id="GPS_AUTHENTICATION_STATE_ERROR"></a>2</td><td><a href="#GPS_AUTHENTICATION_STATE_ERROR">GPS_AUTHENTICATION_STATE_ERROR</a></td><td>The GPS receiver encountered an error while initializing signal authentication.</td></tr><tr><td><a id="GPS_AUTHENTICATION_STATE_OK"></a>3</td><td><a href="#GPS_AUTHENTICATION_STATE_OK">GPS_AUTHENTICATION_STATE_OK</a></td><td>The GPS receiver has correctly authenticated all signals.</td></tr><tr><td><a id="GPS_AUTHENTICATION_STATE_DISABLED"></a>4</td><td><a href="#GPS_AUTHENTICATION_STATE_DISABLED">GPS_AUTHENTICATION_STATE_DISABLED</a></td><td>GPS signal authentication is disabled on the receiver.</td></tr></tbody></table><h3 id="GPS_JAMMING_STATE" tabindex="-1">GPS_JAMMING_STATE — [WIP] <a class="header-anchor" href="#GPS_JAMMING_STATE" aria-label="Permalink to &quot;GPS_JAMMING_STATE — [WIP] {#GPS_JAMMING_STATE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Signal jamming state in a GPS receiver.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="GPS_JAMMING_STATE_UNKNOWN"></a>0</td><td><a href="#GPS_JAMMING_STATE_UNKNOWN">GPS_JAMMING_STATE_UNKNOWN</a></td><td>The GPS receiver does not provide GPS signal jamming info.</td></tr><tr><td><a id="GPS_JAMMING_STATE_OK"></a>1</td><td><a href="#GPS_JAMMING_STATE_OK">GPS_JAMMING_STATE_OK</a></td><td>The GPS receiver detected no signal jamming.</td></tr><tr><td><a id="GPS_JAMMING_STATE_MITIGATED"></a>2</td><td><a href="#GPS_JAMMING_STATE_MITIGATED">GPS_JAMMING_STATE_MITIGATED</a></td><td>The GPS receiver detected and mitigated signal jamming.</td></tr><tr><td><a id="GPS_JAMMING_STATE_DETECTED"></a>3</td><td><a href="#GPS_JAMMING_STATE_DETECTED">GPS_JAMMING_STATE_DETECTED</a></td><td>The GPS receiver detected signal jamming.</td></tr></tbody></table><h3 id="GPS_SPOOFING_STATE" tabindex="-1">GPS_SPOOFING_STATE — [WIP] <a class="header-anchor" href="#GPS_SPOOFING_STATE" aria-label="Permalink to &quot;GPS_SPOOFING_STATE — [WIP] {#GPS_SPOOFING_STATE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Signal spoofing state in a GPS receiver.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="GPS_SPOOFING_STATE_UNKNOWN"></a>0</td><td><a href="#GPS_SPOOFING_STATE_UNKNOWN">GPS_SPOOFING_STATE_UNKNOWN</a></td><td>The GPS receiver does not provide GPS signal spoofing info.</td></tr><tr><td><a id="GPS_SPOOFING_STATE_OK"></a>1</td><td><a href="#GPS_SPOOFING_STATE_OK">GPS_SPOOFING_STATE_OK</a></td><td>The GPS receiver detected no signal spoofing.</td></tr><tr><td><a id="GPS_SPOOFING_STATE_MITIGATED"></a>2</td><td><a href="#GPS_SPOOFING_STATE_MITIGATED">GPS_SPOOFING_STATE_MITIGATED</a></td><td>The GPS receiver detected and mitigated signal spoofing.</td></tr><tr><td><a id="GPS_SPOOFING_STATE_DETECTED"></a>3</td><td><a href="#GPS_SPOOFING_STATE_DETECTED">GPS_SPOOFING_STATE_DETECTED</a></td><td>The GPS receiver detected signal spoofing but still has a fix.</td></tr></tbody></table><h3 id="GPS_RAIM_STATE" tabindex="-1">GPS_RAIM_STATE — [WIP] <a class="header-anchor" href="#GPS_RAIM_STATE" aria-label="Permalink to &quot;GPS_RAIM_STATE — [WIP] {#GPS_RAIM_STATE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>State of RAIM processing.</p><table tabindex="0"><thead><tr><th>值</th><th>Name</th><th>描述</th></tr></thead><tbody><tr><td><a id="GPS_RAIM_STATE_UNKNOWN"></a>0</td><td><a href="#GPS_RAIM_STATE_UNKNOWN">GPS_RAIM_STATE_UNKNOWN</a></td><td>RAIM capability is unknown.</td></tr><tr><td><a id="GPS_RAIM_STATE_DISABLED"></a>1</td><td><a href="#GPS_RAIM_STATE_DISABLED">GPS_RAIM_STATE_DISABLED</a></td><td>RAIM is disabled.</td></tr><tr><td><a id="GPS_RAIM_STATE_OK"></a>2</td><td><a href="#GPS_RAIM_STATE_OK">GPS_RAIM_STATE_OK</a></td><td>RAIM integrity check was successful.</td></tr><tr><td><a id="GPS_RAIM_STATE_FAILED"></a>3</td><td><a href="#GPS_RAIM_STATE_FAILED">GPS_RAIM_STATE_FAILED</a></td><td>RAIM integrity check failed.</td></tr></tbody></table><h2 id="mav_commands" tabindex="-1">Commands (MAV_CMD) <a class="header-anchor" href="#mav_commands" aria-label="Permalink to &quot;Commands (MAV_CMD) {#mav_commands}&quot;">​</a></h2><h3 id="MAV_CMD_DO_FIGURE_EIGHT" tabindex="-1">MAV_CMD_DO_FIGURE_EIGHT (35) — [WIP] <a class="header-anchor" href="#MAV_CMD_DO_FIGURE_EIGHT" aria-label="Permalink to &quot;MAV_CMD_DO_FIGURE_EIGHT (35) — [WIP] {#MAV_CMD_DO_FIGURE_EIGHT}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Fly a figure eight path as defined by the parameters.</p><p>Set parameters to NaN/INT32_MAX (as appropriate) to use system-default values. The command is intended for fixed wing vehicles (and VTOL hybrids flying in fixed-wing mode), allowing POI tracking for gimbals that don&#39;t support infinite rotation. This command only defines the flight path. Speed should be set independently (use e.g. <a href="#MAV_CMD_DO_CHANGE_SPEED">MAV_CMD_DO_CHANGE_SPEED</a>). Yaw and other degrees of freedom are not specified, and will be flight-stack specific (on vehicles where they can be controlled independent of the heading).</p><table tabindex="0"><thead><tr><th>Param (Label)</th><th>描述</th><th>Units</th></tr></thead><tbody><tr><td>1 (Major Radius)</td><td>Major axis radius of the figure eight. Positive: orbit the north circle clockwise. Negative: orbit the north circle counter-clockwise.<br>NaN: The radius will be set to 2.5 times the minor radius and direction is clockwise.<br>Must be greater or equal to two times the minor radius for feasible values.</td><td>m</td></tr><tr><td>2 (Minor Radius)</td><td>Minor axis radius of the figure eight. Defines the radius of the two circles that make up the figure. Negative value has no effect.<br>NaN: The radius will be set to the default loiter radius.</td><td>m</td></tr><tr><td>3</td><td></td><td></td></tr><tr><td>4 (Orientation)</td><td>Orientation of the figure eight major axis with respect to true north (range: [-pi,pi]). NaN: use default orientation aligned to true north.</td><td>rad</td></tr><tr><td>5 (Latitude/X)</td><td>Center point latitude/X coordinate according to MAV_FRAME. If no MAV_FRAME specified, MAV_FRAME_GLOBAL is assumed.<br>INT32_MAX or NaN: Use current vehicle position, or current center if already loitering.</td><td></td></tr><tr><td>6 (Longitude/Y)</td><td>Center point longitude/Y coordinate according to MAV_FRAME. If no MAV_FRAME specified, MAV_FRAME_GLOBAL is assumed.<br>INT32_MAX or NaN: Use current vehicle position, or current center if already loitering.</td><td></td></tr><tr><td>7 (Altitude/Z)</td><td>Center point altitude MSL/Z coordinate according to MAV_FRAME. If no MAV_FRAME specified, MAV_FRAME_GLOBAL is assumed.<br>INT32_MAX or NaN: Use current vehicle altitude.</td><td></td></tr></tbody></table><h3 id="MAV_CMD_DO_UPGRADE" tabindex="-1">MAV_CMD_DO_UPGRADE (247) — [WIP] <a class="header-anchor" href="#MAV_CMD_DO_UPGRADE" aria-label="Permalink to &quot;MAV_CMD_DO_UPGRADE (247) — [WIP] {#MAV_CMD_DO_UPGRADE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Request a target system to start an upgrade of one (or all) of its components.</p><p>For example, the command might be sent to a companion computer to cause it to upgrade a connected flight controller. The system doing the upgrade will report progress using the normal command protocol sequence for a long running operation. Command protocol information: <a href="https://mavlink.io/en/services/command.html" target="_blank" rel="noreferrer">https://mavlink.io/en/services/command.html</a>.</p><table tabindex="0"><thead><tr><th>Param (Label)</th><th>描述</th><th>值</th></tr></thead><tbody><tr><td>1 (Component ID)</td><td>Component id of the component to be upgraded. If set to 0, all components should be upgraded.</td><td><a href="#MAV_COMPONENT">MAV_COMPONENT</a></td></tr><tr><td>2 (Reboot)</td><td>0: Do not reboot component after the action is executed, 1: Reboot component after the action is executed.</td><td>min: 0 max: 1 inc: 1</td></tr><tr><td>3</td><td>Reserved</td><td></td></tr><tr><td>4</td><td>Reserved</td><td></td></tr><tr><td>5</td><td>Reserved</td><td></td></tr><tr><td>6</td><td>Reserved</td><td></td></tr><tr><td>7</td><td>WIP: upgrade progress report rate (can be used for more granular control).</td><td></td></tr></tbody></table><h3 id="MAV_CMD_DO_SET_STANDARD_MODE" tabindex="-1">MAV_CMD_DO_SET_STANDARD_MODE (262) — [WIP] <a class="header-anchor" href="#MAV_CMD_DO_SET_STANDARD_MODE" aria-label="Permalink to &quot;MAV_CMD_DO_SET_STANDARD_MODE (262) — [WIP] {#MAV_CMD_DO_SET_STANDARD_MODE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Enable the specified standard MAVLink mode.</p><p>If the mode is not supported the vehicle should ACK with <a href="#MAV_RESULT_FAILED">MAV_RESULT_FAILED</a>.</p><table tabindex="0"><thead><tr><th>Param (Label)</th><th>描述</th><th>值</th></tr></thead><tbody><tr><td>1 (Standard Mode)</td><td>The mode to set.</td><td><a href="#MAV_STANDARD_MODE">MAV_STANDARD_MODE</a></td></tr><tr><td>2</td><td></td><td></td></tr><tr><td>3</td><td></td><td></td></tr><tr><td>4</td><td></td><td></td></tr><tr><td>5</td><td></td><td></td></tr><tr><td>6</td><td></td><td></td></tr><tr><td>7</td><td></td><td></td></tr></tbody></table><h3 id="MAV_CMD_SET_AT_S_PARAM" tabindex="-1">MAV_CMD_SET_AT_S_PARAM (550) — [WIP] <a class="header-anchor" href="#MAV_CMD_SET_AT_S_PARAM" aria-label="Permalink to &quot;MAV_CMD_SET_AT_S_PARAM (550) — [WIP] {#MAV_CMD_SET_AT_S_PARAM}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Allows setting an AT S command of an SiK radio.</p><table tabindex="0"><thead><tr><th>Param (Label)</th><th>描述</th></tr></thead><tbody><tr><td>1 (Radio instance)</td><td>The radio instance, one-based, 0 for all.</td></tr><tr><td>2 (Index)</td><td>The Sx index, e.g. 3 for S3 which is NETID.</td></tr><tr><td>3 (Value)</td><td>The value to set it to, e.g. default 25 for NETID</td></tr><tr><td>4</td><td></td></tr><tr><td>5</td><td></td></tr><tr><td>6</td><td></td></tr><tr><td>7</td><td></td></tr></tbody></table><h3 id="MAV_CMD_DO_SET_SYS_CMP_ID" tabindex="-1">MAV_CMD_DO_SET_SYS_CMP_ID (610) — [WIP] <a class="header-anchor" href="#MAV_CMD_DO_SET_SYS_CMP_ID" aria-label="Permalink to &quot;MAV_CMD_DO_SET_SYS_CMP_ID (610) — [WIP] {#MAV_CMD_DO_SET_SYS_CMP_ID}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Set system and component id. This allows moving of a system and all its components to a new system id, or moving a particular component to a new system/component id. Recipients must reject command addressed to broadcast system ID.</p><table tabindex="0"><thead><tr><th>Param (Label)</th><th>描述</th><th>值</th></tr></thead><tbody><tr><td>1 (System ID)</td><td>New system ID for target component(s). 0: ignore and reject command (broadcast system ID not allowed).</td><td>min: 1 max: 255 inc: 1</td></tr><tr><td>2 (Component ID)</td><td>New component ID for target component(s). 0: ignore (component IDs don&#39;t change).</td><td>min: 0 max: 255 inc: 1</td></tr><tr><td>3 (Reboot)</td><td>Reboot components after ID change. Any non-zero value triggers the reboot.</td><td></td></tr><tr><td>4</td><td></td><td></td></tr></tbody></table><h3 id="MAV_CMD_PARAM_TRANSACTION" tabindex="-1">MAV_CMD_PARAM_TRANSACTION (900) — [WIP] <a class="header-anchor" href="#MAV_CMD_PARAM_TRANSACTION" aria-label="Permalink to &quot;MAV_CMD_PARAM_TRANSACTION (900) — [WIP] {#MAV_CMD_PARAM_TRANSACTION}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Request to start or end a parameter transaction. Multiple kinds of transport layers can be used to exchange parameters in the transaction (param, param_ext and mavftp). The command response can either be a success/failure or an in progress in case the receiving side takes some time to apply the parameters.</p><table tabindex="0"><thead><tr><th>Param (Label)</th><th>描述</th><th>值</th></tr></thead><tbody><tr><td>1 (Action)</td><td>Action to be performed (start, commit, cancel, etc.)</td><td><a href="#PARAM_TRANSACTION_ACTION">PARAM_TRANSACTION_ACTION</a></td></tr><tr><td>2 (Transport)</td><td>Possible transport layers to set and get parameters via mavlink during a parameter transaction.</td><td><a href="#PARAM_TRANSACTION_TRANSPORT">PARAM_TRANSACTION_TRANSPORT</a></td></tr><tr><td>3 (Transaction ID)</td><td>Identifier for a specific transaction.</td><td></td></tr></tbody></table><h3 id="MAV_CMD_ODID_SET_EMERGENCY" tabindex="-1">MAV_CMD_ODID_SET_EMERGENCY (12900) — [WIP] <a class="header-anchor" href="#MAV_CMD_ODID_SET_EMERGENCY" aria-label="Permalink to &quot;MAV_CMD_ODID_SET_EMERGENCY (12900) — [WIP] {#MAV_CMD_ODID_SET_EMERGENCY}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Used to manually set/unset emergency status for remote id.</p><p>This is for compliance with MOC ASTM docs, specifically F358 section 7.7: &quot;Emergency Status Indicator&quot;. The requirement can also be satisfied by automatic setting of the emergency status by flight stack, and that approach is preferred. See <a href="https://mavlink.io/en/services/opendroneid.html" target="_blank" rel="noreferrer">https://mavlink.io/en/services/opendroneid.html</a> for more information.</p><table tabindex="0"><thead><tr><th>Param (Label)</th><th>描述</th><th>值</th></tr></thead><tbody><tr><td>1 (Number)</td><td>Set/unset emergency 0: unset, 1: set</td><td>min: 0 inc: 1</td></tr><tr><td>2</td><td></td><td></td></tr><tr><td>3</td><td></td><td></td></tr><tr><td>4</td><td>Empty</td><td></td></tr><tr><td>5</td><td>Empty</td><td></td></tr><tr><td>5</td><td>Empty</td><td></td></tr><tr><td>6</td><td>Empty</td><td></td></tr><tr><td>7</td><td>Empty</td><td></td></tr></tbody></table><h3 id="MAV_CMD_REQUEST_OPERATOR_CONTROL" tabindex="-1">MAV_CMD_REQUEST_OPERATOR_CONTROL (32100) — [WIP] <a class="header-anchor" href="#MAV_CMD_REQUEST_OPERATOR_CONTROL" aria-label="Permalink to &quot;MAV_CMD_REQUEST_OPERATOR_CONTROL (32100) — [WIP] {#MAV_CMD_REQUEST_OPERATOR_CONTROL}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Request GCS control of a system (or of a specific component in a system).</p><p>A controlled system should only accept MAVLink commands and command-like messages that are sent by its controlling GCS, or from other components with the same system id. Commands from other systems should be rejected with <a href="#MAV_RESULT_FAILED">MAV_RESULT_FAILED</a> (except for this command, which may be acknowledged with <a href="#MAV_RESULT_ACCEPTED">MAV_RESULT_ACCEPTED</a> if control is granted). Command-like messages should be ignored (or rejected if that is supported by their associated protocol).</p><p>GCS control of the whole system is managed via a single component that we will refer to here as the &quot;system manager component&quot;. This component streams the <a href="#CONTROL_STATUS">CONTROL_STATUS</a> message and sets the <a href="#GCS_CONTROL_STATUS_FLAGS_SYSTEM_MANAGER">GCS_CONTROL_STATUS_FLAGS_SYSTEM_MANAGER</a> flag. Other components in the system should monitor for the <a href="#CONTROL_STATUS">CONTROL_STATUS</a> message with this flag, and set their controlling GCS to match its published system id. A GCS that wants to control the system should also monitor for the same message and flag, and address the <a href="#MAV_CMD_REQUEST_OPERATOR_CONTROL">MAV_CMD_REQUEST_OPERATOR_CONTROL</a> to its component id. Note that integrators are required to ensure that there is only one system manager component in the system (i.e. one component emitting the message with <a href="#GCS_CONTROL_STATUS_FLAGS_SYSTEM_MANAGER">GCS_CONTROL_STATUS_FLAGS_SYSTEM_MANAGER</a> set).</p><p>The <a href="#MAV_CMD_REQUEST_OPERATOR_CONTROL">MAV_CMD_REQUEST_OPERATOR_CONTROL</a> command is sent by a GCS to the system manager component to request or release control of a system, specifying whether subsequent takeover requests from another GCS are automatically granted, or require permission.</p><p>The system manager component should grant control to the GCS if the system does not require takeover permission (or is uncontrolled) and ACK the request with <a href="#MAV_RESULT_ACCEPTED">MAV_RESULT_ACCEPTED</a>. The system manager component should then stream <a href="#CONTROL_STATUS">CONTROL_STATUS</a> indicating its controlling system: all other components with the same system id should monitor this message and set their own controlling GCS to match that of the system manager component.</p><p>If the system manager component cannot grant control (because takeover requires permission), the request should be rejected with <a href="#MAV_RESULT_FAILED">MAV_RESULT_FAILED</a>. The system manager component should then send this same command to the current owning GCS in order to notify of the request. The owning GCS would ACK with <a href="#MAV_RESULT_ACCEPTED">MAV_RESULT_ACCEPTED</a>, and might choose to release control of the vehicle, or re-request control with the takeover bit set to allow permission. In case it choses to re-request control with takeover bit set to allow permission, requestor GCS will only have 10 seconds to get control, otherwise owning GCS will re-request control with takeover bit set to disallow permission, and requestor GCS will need repeat the request if still interested in getting control. Note that the pilots of both GCS should co-ordinate safe handover offline.</p><p>Note that in most systems the only controlled component will be the &quot;system manager component&quot;, and that will be the autopilot. However separate GCS control of a particular component is also permitted, if supported by the component. In this case the GCS will address <a href="#MAV_CMD_REQUEST_OPERATOR_CONTROL">MAV_CMD_REQUEST_OPERATOR_CONTROL</a> to the specific component it wants to control. The component will then stream <a href="#CONTROL_STATUS">CONTROL_STATUS</a> for its controlling GCS (it must not set <a href="#GCS_CONTROL_STATUS_FLAGS_SYSTEM_MANAGER">GCS_CONTROL_STATUS_FLAGS_SYSTEM_MANAGER</a>). The component should fall back to the system GCS (if any) when it is not directly controlled, and may stop emitting <a href="#CONTROL_STATUS">CONTROL_STATUS</a>. The flow is otherwise the same as for requesting control over the whole system.</p><table tabindex="0"><thead><tr><th>Param (Label)</th><th>描述</th><th>值</th><th>Units</th></tr></thead><tbody><tr><td>1 (Sysid requesting control)</td><td>System ID of GCS requesting control. 0 when command sent from GCS to autopilot (autopilot determines requesting GCS sysid from message header). Sysid of GCS requesting control when command sent by autopilot to controlling GCS.</td><td></td><td></td></tr><tr><td>2 (Action)</td><td>0: Release control, 1: Request control.</td><td></td><td></td></tr><tr><td>3 (Allow takeover)</td><td>Enable automatic granting of ownership on request (by default reject request and notify current owner). 0: Ask current owner and reject request, 1: Allow automatic takeover.</td><td></td><td></td></tr><tr><td>4 (Request timeout)</td><td>Timeout in seconds before a request to a GCS to allow takeover is assumed to be rejected. This is used to display the timeout graphically on requestor and GCS in control.</td><td>min: 3 max: 60</td><td>s</td></tr><tr><td>5</td><td>Empty</td><td></td><td></td></tr><tr><td>6</td><td>Empty</td><td></td><td></td></tr><tr><td>7</td><td>Empty</td><td></td><td></td></tr></tbody></table><h3 id="MAV_CMD_EXTERNAL_WIND_ESTIMATE" tabindex="-1">MAV_CMD_EXTERNAL_WIND_ESTIMATE (43004) — [WIP] <a class="header-anchor" href="#MAV_CMD_EXTERNAL_WIND_ESTIMATE" aria-label="Permalink to &quot;MAV_CMD_EXTERNAL_WIND_ESTIMATE (43004) — [WIP] {#MAV_CMD_EXTERNAL_WIND_ESTIMATE}&quot;">​</a></h3><p><span class="warning"><strong>WORK IN PROGRESS</strong>: Do not use in stable production environments (it may change).</span></p><p>Set an external estimate of wind direction and speed.</p><p>This might be used to provide an initial wind estimate to the estimator (EKF) in the case where the vehicle is wind dead-reckoning, extending the time when operating without GPS before before position drift builds to an unsafe level. For this use case the command might reasonably be sent every few minutes when operating at altitude, and the value is cleared if the estimator resets itself.</p><table tabindex="0"><thead><tr><th>Param (Label)</th><th>描述</th><th>值</th><th>Units</th></tr></thead><tbody><tr><td>1 (Wind speed)</td><td>Horizontal wind speed.</td><td>min: 0</td><td>m/s</td></tr><tr><td>2 (Wind speed accuracy)</td><td>Estimated 1 sigma accuracy of wind speed. Set to NaN if unknown.</td><td></td><td>m/s</td></tr><tr><td>3 (Direction)</td><td>Azimuth (relative to true north) from where the wind is blowing.</td><td>min: 0 max: 360</td><td>度</td></tr><tr><td>4 (Direction accuracy)</td><td>Estimated 1 sigma accuracy of wind direction. Set to NaN if unknown.</td><td></td><td>度</td></tr><tr><td>5</td><td>Empty</td><td></td><td></td></tr><tr><td>6</td><td>Empty</td><td></td><td></td></tr><tr><td>7</td><td>Empty</td><td></td><td></td></tr></tbody></table>',193)]))}const S=e(o,[["render",n]]);export{l as __pageData,S as default};
